INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (325, 'Set de cubiertos reutilizables de línea nature fabricados en coco. Tras el consumo de su fruto, del coco y su planta se pueden obtener multitud de productos, contribuyendo a la sostenibilidad del planeta y fomentando el uso de materias primas naturales. Incluye cuchara, cuchillo y tenedor. Presentados en bolsita individual de algodón con cierre de cordón.', '1063', null);
