INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (377, 'Identificador en cartón reciclado. Incluye tarjeta con semillas de flores silvestres, convirtiéndolo así en papel plantable al final de su vida útil. Flores silvestres en colores surtidos, con instrucciones de plantado en el interior y distintivo Made in Europe.', '2643', null);
