INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (407, 'Tarro de 710ml de capacidad en resistente PP especialmente diseñado para líquidos. En variada gama en vivos colores, tapadera translúcida con 4 cierres, anilla de seguridad interior y dosificador integrado. Asa ergonómica a juego', '4873', null);
