INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (417, 'Divertida comba con cuerda extra larga de 240cm de largo en vivos colores. Mangos de sujeción en resistente madera barnizada. Presentación plegada.', '5904', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (418, ' 240cm length jumping rope in bright tones and with comfortable, lacquered, wooden handles. Folded presentation.', '5904', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (419, 'Taza para colorear de 320ml de capacidad. Carcasa exterior de la taza con cubierta transparente extraíble para insertar la lámina coloreada y parte interior en PP de color blanco. Con 4 láminas para pintar y 4 lápices de colores. Presentado en caja individual.', '6020', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (420, ' Mug for coloring. 320 ml capacity, with removable outer shell in transparent material to insert a colored sheet, Inside in solid finishing, white color PP material. 4 sheets for painting and 4 color pencils. Presented in an individual box.', '6020', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (421, 'Mochila trolley en resistente poliéster 300D. Con conexión USB -powerbank no incuido- y bolsillo interior acolchado para ordenador portátil de hasta 15 pulgadas y compartimento para tablet de hasta 10 pulgadas. Bolsillo principal con doble tirador, bolsillos lateral y frontal. Compartimento para mecanismo de trolley con cierre de cremallera, asa reforzada y accesorio cubre ruedas con fijación de velcro. Espalda acolchada y cintas de hombros ajustables con acolchado.', '6047', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (422, 'Backpack trolley in resistant polyester 300D. With USB charging port -powerbank not included-, inner padded pocket for laptops up to 15 inches and inner compartment for tablets up to 10 inches. Main pocket with double puller and front and side pockets.Compartment for trolley mechanism with zipper closure, reinforced handle and wheel cover accessory with velcro fastening. Padded back and adjustable shoulder straps with padding.', '6047', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (423, 'Bolsa en resistente material 100% algodón de 140g/m2 con acabado en color natural. Con asas largas de 70cm y acabado cosido. Resistencia hasta 7kg de peso.', '6048', null);
