INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (426, 'Ribbon keyring available in a wide range of color, each one representing a different solidarity purpose.', '6068', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (427, 'Bloc de notas de elegante acabado en color blanco, con memoria USB de 16GB de capacidad integrada en la tapa frontal. Cinta elástica, memoria USB y marcapáginas a juego, en variada gama de colores. Incluye 100 hojas con disposición a una raya.', '6201', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (428, ' Stylish, white color finished notepad with 16GB USB flash drive integrated in the front cover. Elastic ribbon, USB memory and bookmark in matching, vivid colors. Including 100 sheets with striped outline.', '6201', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (429, 'Mochila de seguridad, con diseño urbano en resistente poliéster 300D. Bolsillo interior con protección de seguridad RFID (Identificación por Radio Frecuencia) que mantiene seguros los datos de la tarjeta y demás dispositivos. Compartimento principal con elegante tapeta en la cremallera. Con conexión USB -powerbank no incluido- y bolsillo interior acolchado para ordenador portátil de hasta 15 pulgadas. Parte trasera y cintas acolchadas.', '6215', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (430, 'Security backpack in an urban design and in resistance 300D polyester material. Inside pocket with RFID (Radio Frequency Identification) security technology that keeps card and other devices data safe. Main compartment with an elegant zipper cover. With USB connection -powerbank not included and inside, padded pocket for laptops up to 15 inches. Padded backside and shoulder straps.', '6215', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (431, 'Mochila antirrobo en resistente poliéster 300D. Tejido de alta calidad resistente a cortes y a roces, con bolsillo antirrobo en la espalda para guardar los objetos de valor. Compartimento principal con cierre de cremallera equipado con conexión de powerbank -no incluida- y dos compartimentos interiores independientes y acolchados para portátil de hasta 15 pulgadas y para tablets de hasta 10 pulgadas, además de bolsillos complementarios interiores. Cuenta también con un bolsillo exterior en el frontal con cierre de cremallera. Cómoda de llevar gracias a su espalda acolchada como las asas ergonómicas y ajustables, además con accesorio para trolley.', '6220', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (432, 'Anti-theft backpack in durable 300D polyester. High quality, cut and scratch resistant fabric, with anti-theft pocket on the back to store valuables. Zipped main compartment equipped with powerbank connection - not included - and two separate, padded interior compartments for laptops up to 15" and tablet up to 10", plus additional inside pockets. Features an outside front pocket with zip closure. Comfortable to carry using the padded back and ergonomic, adjustable shoulder straps. With trolley fitting accessory.', '6220', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (433, 'Navaja multiusos de orizons con accesorios de acero inox de 10 funciones y mango de aluminio en vivos colores. Con navaja, tijeras, abrelatas, abrebotellas con destornillador plano, destornillador de punta de estrella, punzón con lima y sacacorchos. Con anilla para llavero y presentada en atractiva caja de diseño con logotipo de la marca.', '7292', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (434, 'Multi purpose knife by Orizons with stainless steel accessories of 10 functions and aluminum handle in bright tones. With knife, scissors, can opener, bottle opener with flat screwdriver, crosshead screwdriver, file punch and corkscrew. With ring for keychain and presented in an attractive design box with the brand logo.', '7292', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (435, 'Navaja de Orizons de acero inox y resistente empuñadura anti deslizante con bloqueo de seguridad. Con linterna led y troquelado en mango. Pilas de botón incluidas y presentada en caja individual con logotipo de la marca.', '7340', null);
