INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (453, 'Bidón de 650ml de capacidad. Fabricado con cuerpo de cristal, disponible en variada gama de colores, y tapón a rosca de acero inox y acabado cromado. Presentado en caja individual de atractivo diseño.', '6867', null);
