INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (477, ' Reloj inteligente multifunción de diseño casual, con conexión Bluetooth® y recargable mediante conexión inalámbrica. Elegante carcasa metálica, con correa en material TPU y pantalla TFT táctil de 1,69 pulgadas, que lo convierten en el gadget perfecto para todo tipo de actividades deportivas, de ocio y para el día a día.  Resistente a salpicaduras de agua, con APP disponible para iOS y Android. Base de carga incluida y presentado en atractiva caja de diseño. Disponible manual de instrucciones en español e inglés.', '1433', null);
