INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (693, 'Soporte plegable para smartphones y tablets. Regulable en altura, incluye protecciones de goma antideslizantes en base y soporte. En sobrio acabado de color blanco y con amplia superficie de marcaje, especialmente indicado para impresión en tampografía. Presentado en atractiva caja de diseño.', '1082', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (694, 'Foldable holder for smartphones and tablets. Adjustable in height, including non-slip rubber protections at the base and holder. In a stylish white finish, with a wide branding surface, especially recommended for pad printing. Presented in an attractive design box.', '1082', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (695, 'Cargador inalámbrico de carga rápida 15W. En elegante acabado blanco, con base de carga con altura regulable y función soporte para smartphone. Compatible con dispositivos equipados con tecnología de carga inalámbrica. Cable de carga Tipo C incluido, con amplia superficie de marcaje y presentado en atractiva caja de diseño. Disponible manual de instrucciones en español e inglés.  En cumplimiento con los requisitos de seguridad: Sistema de protección contra sobrecalentamiento.Sistema de protección contra sobrecarga.Sistema de bloqueo para evitar cortocircuitos.', '1083', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (696, '15W fast charging wireless charger. In stylish white finish, with height-adjustable charging base and smartphone holder function. Compatible with devices equipped with wireless charging technology. Type C charging cable included, with wide branding surface and presented in an attractive design box. Available instruction manual in English and Spanish.  Compliant with safety requirements:Overheat protection system.Overcharge protection system.Blocking system to prevent short-circuits.', '1083', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (697, ' Paraguas plegable de línea nature de 103cm de diámetro. En material pongee 190T RPET, elaborado a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta. De apertura y cierre automáticos, con mecanismo anti viento y varillas de fibra de vidrio. Mango de bambú con botón, distintivo RPET en etiqueta y disponible en variada gama de colores. Funda RPET a juego incluida.', '1088', null);
