INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (753, ' Mochila de cuerdas de suave poliéster 210T. Fabricada empleando tejido con gran capacidad para reflejar intensamente la luz, gracias a millones de pigmentos reflectantes y basándose en el principio de la retro-reflexión de la luz, una propiedad de visibilidad nocturna que consiste en la refracción de la luz hacia la fuente de origen de la misma. Con cuerdas autocierre en color negro y argollas metálicas. Resistencia hasta 7kg de peso y resistente al agua.', '1137', null);
