INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (904, ' Practical, nature line, double compartment cooler bag. Manufactured in high quality 600D RPET polyester, made from recycled plastic to encourage the reuse of plastic waste and contribute to the sustainability of the planet. Reinforced carry handle, including 2 compartments with zipper closure and double fabric puller, with RPET emblem on outside label:  Upper compartment designed to keep the heat, with isothermal inside in aluminum.  Lower compartment designed to keep the cold, with isothermal inside in PEVA.', '1247', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (905, 'Bolsa nevera en non-woven laminado. Compartimento principal con cierre de velcro e interior isotermo de aluminio. De acabado termosellado, con fuelle y asas reforzadas. Disponible en variada gama de colores.', '1248', null);
