INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1208, ' Padded laptop sleeve for laptops up to 15 inches. In nature line and made of 100% recycled cotton GRS 275g/m2. Main compartment with zipper closure and recycled cotton logo on the outside label. GRS (Global Recycled Standard) certification available, guaranteeing the recycled origin of the material used to manufacture the product. Available in a wide range of colors.', '1449', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1209, ' Portadocumentos de línea nature y fabricado en algodón 100% reciclado GRS de 275g/m2. Compartimento principal y bolsillo exterior con cierre de cremallera y distintivo algodón reciclado en etiqueta exterior. Disponible certificación GRS (Global Recycled Standard / Estándar de Reciclaje Global), que garantiza el origen reciclado del material usado para fabricar el producto. Disponible en variada gama de colores.', '1450', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1210, ' Nature line document holder made of 100% recycled cotton GRS 275g/m2. Main compartment and outside pocket with zipper closure and recycled cotton logo on the outside label. GRS (Global Recycled Standard) certification available, guaranteeing the recycled origin of the material used to manufacture the product. Available in a wide range of colors.', '1450', null);
