INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1213, ' Bolsita de línea nature y tamaño 10x14 cm. Con cierre de cordón y fabricada en 100% algodón reciclado GRS de 120g/m2. Fácil marcaje y especialmente diseñada para pequeños objetos, con distintivo algodón reciclado en etiqueta exterior. Disponible certificación GRS (Global Recycled Standard / Estándar de Reciclaje Global), que garantiza el origen reciclado del material usado para fabricar el producto. Disponible en variada gama de colores.', '1452', null);
