INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1215, 'Petaca de línea nature y 170 ml de capacidad. Exterior en bambú de suave acabado, con cuerpo de acero inox y tapón de seguridad a rosca. De diseño ergonómico para guardar cómodamente en el bolsillo. Presentada en caja individual de diseño eco en cartón kraft.', '1454', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1216, 'Flask in nature line and 170 ml capacity. Outside in bamboo with soft finish, stainless steel body and safety screw cap. Ergonomically designed to fit comfortably in your pocket. Presented in an individual box of eco design in kraft cardboard.', '1454', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1217, 'Bolígrafo lápiz eterno 2 en 1, fabricado en resistente metal de acabado brillo. Con cierre de capucha, incluye bolígrafo en un extremo (carga jumbo y tinta azul), y lápiz eterno en el otro extremo, para así usar siempre el mismo lápiz y no generar residuos. Presentado en estuche individual negro.', '1455', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1218, ' Ball pen and eternal pencil 2 in 1, made in durable metal with glossy finish. With cap closure, including ball pen on one end (jumbo cartridge and blue ink), and eternal pencil on the opposite end, to always use the same pencil and not produce waste. Presented in an individual black case.', '1455', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1219, 'Cocktelera de 550 ml de capacidad en acero inox y recubrimiento exterior de cobre galvanizado. Con cierre de seguridad y tapón dosificador en parte superior. Presentada en atractiva caja individual de diseño.', '1457', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1220, 'Cocktail shaker of 550 ml capacity in stainless steel with outside coating of galvanized copper. With safety lock and dosing cap on top. Presented in an attractive individual box.', '1457', null);
