INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1275, ' Lápiz puntero eterno, fabricado en resistente aluminio de acabado metalizado y con anilla cromada. Reutilizable y con escritura infinita, para así usar siempre el mismo lápiz y no generar residuos. Incluye goma de borrar en la parte interior del lápiz y está disponible en variada gama de colores.', '1489', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1276, ' Eternal pointer pencil, made of resistant aluminum with metallic finish and chrome-plated ring. Reusable and with infinite writing, to always use the same pencil and not produce waste. Including eraser on the inside of the pencil and available in a wide range of colors.', '1489', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1277, 'Auriculares intraurales de línea nature. Fabricados en resistente ABS con detalles en bambú en auriculares y base. Conexión Bluetooth® 5.0 y función manos libres, con botón de control táctil en el auricular. Incluye cargador inalámbrico 10W integrado en la tapa de bambú de la base de caga. Recargables por USB, con cable Tipo C incluido. Presentados en caja individual de diseño eco en cartón kraft.', '1492', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1278, 'Nature line earphones. Made in resistant ABS with bamboo details in earphones and base. Bluetooth® 5.0 connection and hands-free function, with touch control button on the earphone. 10W wireless charger integrated in the bamboo lid of the base and rechargeable via USB, with Type C cable included. Presented in an individual eco-design box in kraft cardboard.', '1492', null);
