INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1280, 'Genuine, nature line magnetic opener, made in resistant bamboo, with metal opening mechanism.', '1493', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1281, ' Libreta soporte de anillas de línea nature. Tapas rígidas fabricadas en resistente material RPET 300D, elaborado a partir de plástico reciclado. Con original soporte para smartphone y tablet integrado en la portada y fabricado en caña de trigo. Incluye elástico de cierre y soporte para bolígrafo a juego. 70 hojas interiores con disposición a una raya y disponible en variada gama de colores. Con distintivo RPET en contraportada y presentada en bolsa individual reciclada.', '1494', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1282, ' Nature line ring binder notebook. Hard covers made in resistant RPET 300D material, made from recycled plastic. With original smartphone and tablet holder integrated in the cover and made of wheat straw. Including elastic closure and matching pen holder. 70 pages inside with single-stripe layout and available in a wide range of colors. With RPET logo on the back cover and presented in an individual recycled bag.', '1494', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1283, ' Caja de presentación especialmente diseñada para bidones. Tamaño 8x26,7x8 cm y fabricada en cartón reciclado corrugado de acabado kraft. En línea con la tendencia del mercado hacia la sostenibilidad integral del regalo, ofreciendo soluciones de origen reciclado con el fin de ayudar a la mejora y conservación de nuestro planeta.', '1497', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1284, ' Presentation box specially designed for bottles. Size 8x26,7x8 cm, made of recycled, corrugated cardboard with a kraft finish. In line with the market trend towards integral gift sustainability, offering solutions of recycled origin, aimed at supporting the improvement and conservation of our planet.', '1497', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1285, 'Original vela de línea nature con aroma vainilla. Base fabricada en madera natural, presentada en atractiva caja individual de diseño eco y acabado kraft.', '1498', null);
