INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1289, 'Comedero colgante para pájaro. Con dos recipientes fabricados en fibra de bambú (medidas 15 x 4 cm cada uno) y resistente cuerda de algodón natural. Presentación en caja individual de diseño kraft.', '1500', null);
