INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1296, 'Compact and portable two-compartment bowl. Outside made of durable 600D RPET polyester with reinforced piping. Waterproof inside. With carrying carabiner and zip closure with metal puller. Available in a wide range of colours.', '1503', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1297, 'Toalla para mascotas fabricada en resistente poliéster de 160g/m2. Medida 66 x 106 cm, con dos prácticos bolsillos para las manos, facilitando así el secado de la mascota. Presentación en funda individual de redecilla con cierre de cordón y ajustador.', '1504', null);
