INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1307, ' Juego de pelota y palas de disco. Incluye pelota con ventosas y palas de disco lisas, fabricadas en resistente caña de trigo, con cinta elástica para las manos a juego.  La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '1509', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1308, ' Set of ball and disc paddles. Including ball with suction cups and flat disc paddles, made of resistant wheat straw, with matching elastic strap for the hand.  Wheat straw promotes the use of natural raw materials, reducing pollutant emissions.', '1509', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1309, 'Par de palas de playa con tableros integrados de ajedrez, damas y parchís. Fabricadas en madera, con mango recubierto en caña de trigo y pelota también en caña de trigo. Incluye figuras de ajedrez, fichas de damas, fichas de parchís y dados. Set presentado en mochila individual 100% algodón con cuerdas de cierre a juego.La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '1510', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1310, 'Pair of beach rackets with integrated chess, checkers and parcheesi boards. Made of wood, with handle coated in wheat straw and ball also in wheat straw. Including chess pieces, checkers, parcheesi pieces and dice. Set presented in an individual 100% cotton backpack with matching closing strings.Wheat straw promotes the use of natural raw materials, reducing pollutant emissions.', '1510', null);
