INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1445, 'Gafas de sol de línea nature con protección UV 400. Montura fabricada en fibra de café y patillas en madera natural a juego, con lentes en tono marrón. Distintivo fibra de café inyectado en molde de patilla izquierda. Presentación en caja individual de diseño kraft.La fibra de café es una solución circular que se obtiene de la utilización de los residuos producidos en la obtención del café. Una alternativa sostenible que favorece la reducción del uso de plásticos, ahorrando energía en su proceso de fabricación y contribuyendo al cuidado del medio ambiente.', '1603', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1446, 'Nature line sunglasses with UV 400 protection. Coffee fibre frame and matching natural wood arms, with brown lenses. Coffee fibre distinctive injected in mould on left arm. Presented in an individual box with kraft design.Coffee fibre is a circular solution, obtained from using the waste generated in the production of coffee. A sustainable alternative, favoring the reduction of plastic use, saving energy in the manufacturing process, and contributing to the protection of the environment.', '1603', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1447, 'Gafas de sol de línea nature con protección UV 400. Fabricadas en RPET, con montura de diseño y lentes efecto espejo. Disponibles en varios colores, con distintivo RPET inyectado en el molde de la patilla izquierda. Presentación en bolsa individual de diseño eco.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '1605', null);
