INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1461, 'Flexómetro 3 m multifunción de línea nature, con carcasa fabrcada en caña de trigo. Con nivel integrado, soporte para bolígrafo (bolígrafo incluido) y bloc con 12 notas adhesivas en color natural. Con freno, clip metálico para cinturón y hoja con medidas en centímetros y pulgadas. Presentación en caja individual de diseño kraft.La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '1618', null);
