INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1620, 'Whisky set in elegant bamboo case with die-cut inside. Including 2 x 320ml glasses, tweezers and 8 reusable natural stone cubes, with cotton pouch. Presented in an individual box with kraft design. The reusable cubes are manufactured in natural steatite stone, also known as soapstone. The drink is kept cold without altering its properties by not diluting its content in water, as well as maintaining its original flavour.', '1706', null);
