INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (1623, 'Set de 10 posavasos de línea nature. Fabricados en fieltro RPET reciclado, fruto de la combinación del proceso de reciclaje de materiales plásticos junto con la técnica milenaria de fabricación del fieltro, dando como resultado un material de eco-tendencia que, además de ser de origen reciclado, también se convierte en reciclable. Soporte fabricado en bambú y posavasos con distintivo RPET. Set presentado en caja individual de diseño kraft.', '1708', null);
