INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (4381, 'Reloj inteligente multifunción de diseño deportivo, con conexión Bluetooth® y recargable mediante cable Micro USB -incluido-. Base extraíble con función auricular y manos libres. Su cuerpo en material TPU y su pantalla LCD integrada de 0,96 pulgadas lo convierten en el gadget perfecto para todo tipo de actividades deportivas, de ocio y para el día a día. Presentado en atractiva caja de diseño.', '6226', null);
