INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (4979, ' Bolígrafo de línea nature en combinación de corcho natural y metal. Con original clip metálico de acabado cromado, a juego con la punta y la anilla. Parte superior en color blanco, de mecanismo giratorio y con tinta azul. Presentado en caja individual de diseño eco.', '6728', null);
