INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (5451, 'Mochila de Antonio Miró de calidad premium en resistente poliéster 600D de sobrio diseño en color negro. Con cierre de cremallera, bolsillo frontal y asa reforzada metálica. Con acolchado ergonómico en espalda y cintas de hombros, incluye compartimento interior acolchado para portátil de hasta 15 pulgadas y para tablets de hasta 10 pulgadas. Fundas interiores para bolígrafos y smartphone. Con logotipo de la marca en frontal.', '7362', null);
