INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (5999, 'Vaso fabricado en PP veteado y de 350ml de capacidad. Tira antiquemaduras y tapa dosificador en TPR. Certificación Food Grade, garantizando la seguridad cuando los alimentos entran en contacto con el material usado para fabricar el producto.', '1159', null);
