INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6121, 'Bolígrafo borrable con el que disfrutar de una escritura fluida y dejar margen para el error. Permite escribir, borrar y reescribir sin dañar los documentos. De cartucho jumbo y en tinta azul. Fabricado en resistente ABS de acabado transparente y disponible en variada gama de colores.', '20150', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6122, 'Erasable ball pen for fluid writing and margin for error. Enables writing, erasing and rewriting without damaging documents. With jumbo cartridge and blue ink. Made of durable ABS with transparent finish, and available in a variety of colours.', '20150', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6123, 'Tierno y suave reno de peluche con bufanda personalizable. Un divertido regalo con motivos navideños con el que sorprender. Presentado en bolsa individual reciclada.', '20135', null);
