INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6125, 'Riñonera de alta calidad y resistente a las salpicaduras de agua en spandex de suave acabado con cremallera en vivos colores, Diseñada para actividades deportivas y de ocio. Con salida para auriculares en parte posterior y cintura elástica con cierre de clic ajustable.', '4866', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6126, ' High quality, splash-resistant, waist bag. In spandex material with a smooth finish and with a grey color zipper. Designed for sports and leisure activities. With adjustable elastic waist bag and buckle closure.', '4866', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6127, ' Poncho para adulto en material LDPE. En medidas 102x127cm y presentado en funda de poliéster RPET, elaborado a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta. Bolsa con mosquetón a juego para fácil transporte y cierre ajustable, disponible en variada gama de colores. Con distintivo RPET en etiqueta exterior.', '1478', null);
