INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6129, 'Lleva el estilo a tus picnics con esta cesta nevera tan cool y retro. Fabricada en mimbre de acabado natural, con cinta bandolera en color marrón. Resistente tapa/mesa en bambú e interior isotermo en aluminio.', '1729', null);
