INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6131, 'Cesta plegable fabricada en resistente 600D RPET. Con resistente estructura de aluminio y 2 asas de transporte plegables en aluminio, con cinta de velcro a juego. Bolsillo exterior con cierre de cremallera y etiqueta exterior con distintivo RPET. El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '1731', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6132, 'Foldable basket made of durable 600D RPET. With resistant aluminium frame and 2 foldable aluminium carrying handles, with matching velcro strap. Outside pocket with zip closure and outside label with the RPET distinctive.RPET material is made from recycled plastic, to encourage the reuse of plastic waste and help the sustainability of the planet.', '1731', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6133, 'Lleva el estilo a tus picnics con esta cesta nevera tan cool y retro. Fabricada en mimbre de acabado natural, con resistente asa de transporte plegable en aluminio. Tapa de diseño vintage con cierre de cremallera e interior isotermo en aluminio.', '1772', null);
