INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6173, 'Lámpara multifunción fabricada en bambú. Con altavoz Bluetooth® 5.0 de 3W de potencia y cargador inalámbrico de 15W. 2LEDs. Compatible con dispositivos móviles de carga inalámbrica, con cable cargador tipo C y viene presentada en una atractiva caja de diseño kraft. Disponible manual de instrucciones en español e inglés.Elaborado con elementos vegetales para así fomentar la utilización de materias primas naturales y reducir las emisiones contaminantes.', '20253', null);
