INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6177, 'Completo multicargador inalámbrico 15W con soporte para gadgets. Diseñado para la carga simultánea del smartphone, auriculares y smartwatch: una estación de carga todo en uno. Presentado en caja de diseño kraft. Disponible manual de instrucciones en español e inglés.', '20264', null);
