INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6260, 'A5 ring binder notebook with hard covers, made in durable recycled cardboard. Inside with 80 white sheets 100% recycled and microperforated. Presented in an individual recycled bag.', '20167', null);
