INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6319, 'Tarro de gran capacidad (1,625 l) con cuerpo fabricado en resistente cristal, que ofrece una resistencia y durabilidad excepcionales, alargando su vida útil y pudiendo ser reutilizado infinidad de veces. Incluye asa para cómodo transporte y funda protectora en suave silicona. Tapa en bambú a presión, con orificio para pajita, y pajita reutilizable de acero inoxidable, con la punta recubierta en silicona para disfrutar de las bebidas de manera cómoda y segura.Presentado en caja individual de diseño kraft.', '20204', null);
