INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6473, 'Chaleco con capucha, en cálido y suave material 100% poliéster. En material ligero, resistente al viento y al agua. Diseño de elegante construcción horizontal y acolchado, con capucha fija integrada. Doble cierre frontal de cremallera y corchetes. Bolsillos laterales con tiradores a juego y cordón ajustable en dobladillo. Disponible en tallas XS, S, M, L, XL, XXL.', '20174', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6474, 'Hooded vest in warm and soft 100% polyester material. In lightweight, wind and water resistant material. Elegant horizontal construction and padded design, with integrated hood. Double front zip and button fastening. Side pockets with matching pull tabs and adjustable hem string. Available in sizes XS, S, M, L, XL, XXL.', '20174', null);
