INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6477, 'Agenda de tamaño A5 fabricada en elegante y sostenible cuero reciclado. Con cubiertas rígidas y elástico de cierre. Con distintivo cuero reciclado.Diseño página día, zona de anotaciones por día y páginas con esquina troquelada para marcar el día. En 2 idiomas: castellano e inglés. Presentado en caja kraft. El cuero reciclado rescata las fibras desechadas en los procesos de fabricación del cuero, minimizando el desperdicio de material, contribuyendo a reducir el impacto ambiental y manteniendo a la vez sus propiedades originales.', '2735', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6478, 'A5 size diary made of elegant and sustainable recycled leather. With hard covers and elastic closure, including recycled leather logo.Day page design, day notes area and pages with die-cut corner to mark the day. In 2 languages: Spanish and English. Presented in a kraft box. Recycled leather rescues the fibres discarded in the leather manufacturing processes, minimising the waste of material, contributing to reduce the environmental impact and, at the same time, keeping its original properties.', '2735', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6479, 'Agenda de tamaño A5 fabricada en corcho natural. Con cubiertas rígidas, cierre magnético fabricado en pu y soporte para bolígrafo a juego. Diseño página día, zona de anotaciones por día y marcapáginas de tela. En 2 idiomas: castellano e inglés. Presentada en caja kraft. Elaborado con elementos vegetales para así fomentar la utilización de materias primas naturales y reducir las emisiones contaminantes.', '2734', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6480, 'A5 size diary made of natural cork. With hard covers, magnetic closure made in PU leather and matching pen holder. Day page design, daily notes area and fabric bookmark. In 2 languages: Spanish and English. Presented in a kraft box. Made with vegetal elements to promote the use of natural raw materials and reduce polluting emissions.', '2734', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6481, 'Agenda de tamaño A5 en cartón reciclado. Con cubiertas rígidas y elástico de cierre.Diseño semana vista, zona de anotaciones y marcapáginas de tela. En 2 idiomas: castellano e inglés. Presentada en caja kraft. El cartón reciclado proviene del reaprovechamiento de excedente de fabricaciones y reciclaje de papel en el final de su vida útil, convirtiéndose en un lienzo ecológico y sostenible.', '2732', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6482, 'A5 size diary in recycled cardboard. With hard covers and elastic closure.Week design, notes area and fabric bookmark. In 2 languages: Spanish and English. Presented in a kraft box. The recycled cardboard comes from the reuse of surplus manufacturing and recycling of paper at the end of its lifespan, making it an ecological and sustainable canvas.', '2732', null);
