INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6565, 'Soporte de rejilla de coche para smartphone fabricado en bambú. Cómoda y segura fijación magnética. Incluye anillo adhesivo. Presentado en caja individual de diseño eco en cartón kraft.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia, con versatilidad de crecimiento y regeneración rápida, siendo una excelente alternativa al uso de madera tradicional.', '20255', null);
