INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6577, 'Elegante set premium que incluye:Termo de doble capa en acero inoxidable reciclado de acabado negro mate y de 500 ml de capacidad, con asa rígida de metal color plata mate. Con distintivo acero inoxidableBloc de notas en RPET en elegante color negro, con detalle metálico especial para marcaje en láser. Incluye cierre elástico y marcapáginas de tela. Interior con 80 hojas de disposición a una raya. Con distintivo RPETParaguas plegable de color negro con 98cm de diámetro. En material pongee 190T RPET. De apertura manual, con sistema anti viento y varillas fibra de vidrio. Con funda de transporte y cintas a juego. Con etiqueta distintiva RPET.Bolígrafo en RPET de acabado negro mate, de mecanismo giratorio y tinta azul. El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta. Set presentado en estuche individual de acabado mate, con troquelado interior para cada uno de los elementos del set.', '20238', null);
