INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6807, 'Mochila de Antonio Miró de calidad premium en resistente polipiel de sobrio diseño en color negro. Con cierre de cremallera doble, bolsillo frontal y asa reforzada acolchada. Con acolchado ergonómico en espalda y cintas de hombros, incluye compartimento interior acolchado para portátil de hasta 15 pulgadas y para tablets de hasta 10 pulgadas. Además, con bolsillo antirrobo con cierre de cremallera en la parte trasera. Fundas interiores para bolígrafos y smartphone. Con logotipo de la marca en frontal.', '7392', null);
