INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6849, 'Cinta de paseo extensible de 3 metros, fabricada en resistente poliéster, con carcasa en robusto ABS. Incluye botón de freno integrado, con cómoda empuñadura ergonómica y práctico gancho para bolsas. Con mosquetón metálico de enganche giratorio y presentada individualmente en caja de diseño kraft.', '20333', null);
