INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6855, 'Manta fabricada en poliéster de 240g/m2, muy confortable y de tacto suave y cálido. Disponible en varios colores y presentada en cinta con lámina para fácil impresión, dentro de una bolsa individual reciclada.', '20336', null);
