INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6857, 'Braga multiposición de alta calidad fabricada en cálido poliéster RPET. Disponible en variados colores jaspeados, con refuerzo en todo el perímetro y etiqueta RPET.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20337', null);
