INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6868, '140cm diameter umbrella in 190T RPET pongee material. Manual opening and windproof mechanism, with fibreglass ribs an velcro closure. In an elegant bla colour, with straight handle made made in bamboo and RPET label.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20342', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6869, 'Paraguas plegable de 120cm de diámetro en material pongee 190T RPET. De apertura y cierre automáticos, con sistema antiviento y varillas en fibra de vidrio. Mango de suave tacto goma, con funda de transporte y cinta a juego. Disponible en variada gama de colores, con etiqueta RPET.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20343', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6870, '120cm diameter folding umbrella in 190T RPET pongee material. Automatic opening and closing, with windproof mechanism and fibreglass ribs. Soft rubber handle, with carrying pouch and matching ribbon. Available in a wide range of colours, with RPET label.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20343', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6871, 'Paraguas de 105cm de diámetro en material poliéster 190T RPET. Apertura automática y varillas de fibra de vidrio antiviento, en variedad de alegres colores en cierre de velcro. Mango recto y caña en suave ABS de tacto gomoso, con etiqueta distintiva RPET.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20344', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6872, '105cm diameter umbrella in 190T RPET polyester material. Automatic opening, with windproof mechanism, velcro fastening and fibreglass ribs. Straight handle in soft ABS with rubber touch, in matching colour with the fabric. With RPET label and available in a wide range of colours.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20344', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6873, 'Confortable braga gorro fabricada en polar fleece de 200g/m2, con tratamiento anti-pilling. Ofrece calidad y suavidad, con interior de pelo para protegerse del frío. De fácil ajuste mediante cordones y disponible en variados colores, con interior siempre en negro.', '20345', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6874, 'Comfortable buff/hat made in polar fleece of 200g/m2, with anti-pilling treatment. Provides quality and softness with fur lining to keep the neck and head warm. Easily adjustable with strings and available in a selection of colours, with black lining.', '20345', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6875, 'Poncho para niño en material PE. En medidas 90x64cm y presentado en funda de poliéster RPET. Bolsa con mosquetón a juego para fácil transporte y cierre ajustable, disponible en variada gama de colores. Con logotipo RPET en etiqueta exterior.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20346', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6876, 'Poncho for children in PE material. Size 90x64cm and presented in a RPET polyester bag, with matching carabiner for easy transport and adjustable closure. Available in a wide range of colours, with RPET logo label.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20346', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6877, 'Caja de presentación formato redondo, fabricada en madera natural y con tapa a juego.', '20347', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6878, 'Presentation box in round format, made of natural wood with matching lid.', '20347', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6879, 'Caja de presentación formato cuadrado, fabricada en madera natural y con tapa a juego.', '20348', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6880, 'Presentation box in square format, made of natural wood with matching lid.', '20348', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6881, 'Identificador para maletas fabricado en fieltro RPET. Para amarrar a cualquier maleta gracias a su cinta flexible e indicar información básica, fabricado en fieltro RPET, con ventana y etiqueta distintiva de RPET.El fieltro RPET es fruto de la combinación del proceso de reciclaje de materiales plásticos junto con la técnica milenaria de fabricación del fieltro, dando como resultado un material de eco-tendencia que, además de ser de origen reciclado, también se convierte en reciclable.', '20349', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6882, 'Luggage identifier made of RPET felt. Designed to be attached to any suitcase using its flexible strap and with a card for basic information. Made in RPET felt, with window and an RPET label.RPET felt is the result of the combination of the recycling processes of plastic materials along with the millenary technique of felt manufacturing, resulting in an eco-trend material that, in addition to being of recycled origin, also becomes recyclable.', '20349', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6883, 'Identificador para maletas fabricado en bambú y silicona veteada. Para sujetar a cualquier maleta gracias a su cinta flexible e indicar información básica. Fabricado en silicona con efecto natural, con placa central en bambú. Presentado en bolsa individual de papel reciclado.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20350', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6884, 'Luggage tag made of bamboo and veined silicone. Designed to be attached to any suitcase using its flexible strap and with a card for basic information. Made of silicone with a natural tone, including a central bamboo plate. Presented in an individual and recycled paper bag.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20350', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6885, 'Cómoda almohadilla portátil acolchada para cuello. Máxima experiencia de confort para cualquier viaje gracias a la almohadilla de suave tacto y los cierres de cordón para un correcto y cómodo ajuste. Con funda confeccionada en RPET para protegerla en el transporte y etiqueta distintiva. El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20351', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6886, 'Comfortable and padded portable neck pad. Ultimate comfort experience for any journey due to the soft touch padding and drawstring fasteners for a correct and comfortable fit. With RPET pouch for protection during transport and label.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20351', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6887, 'Cómoda máscara de viaje fabricada en suave poliéster. Máxima experiencia de descanso para los ojos, debido a que su diseño acolchado proporciona un bloqueo óptimo de la luz para un sueño reparador en cualquier lugar. El elástico regulable garantiza un ajuste cómodo y seguro. Presentado en bolsa individual de papel reciclado.', '20352', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6888, 'Comfortable travel mask made of soft polyester. Maximum resting experience for the eyes, as the cushioned design provides optimal light blocking for a restful sleep anywhere. The adjustable elastic band ensures a comfortable and secure fit. Presented in an individual, recycled paper bag.', '20352', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6889, 'Práctico set de costura, presentado en estuche de bambú, con espejo interior y cierre magnético. Contenido: 2 agujas.Hilo en 6 colores diferentes.2 botones.1 imperdible.1 enhebrador. Presentado en una caja de diseño kraft.', '20353', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6890, 'Practical sewing set, presented in a bamboo case, with mirror inside and magnetic clasp. Contents: 2 needles.Thread in 6 different colours.2 buttons.1 safety pin.1 thread tool. Presented in a kraft design box.', '20353', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6891, 'Mochila multifunción fabricada en 300D RPET. Elaborada con poliéster reciclado y resistente al agua, cuenta con una excelente transpirabilidad en la zona trasera, facilitando la disipación del calor gracias a sus cintas y respaldo acolchados. Las cintas anchas están diseñadas para aliviar la presión en los hombros, permitiendo llevar la mochila cómodamente durante largos periodos.Perfecta para cualquier situación por tener múltiples compartimentos: 2 bolsillos frontales. 3 compartimentos grandes centrales, uno de ellos portatodo, un bolsillo central de gran capacidad para guardad ropa y/o complementos con organizador en su interior, y un tercero acolchado, perfecto para llevar portátil de hasta 17,3 pulgadas. Además, cuenta con 3 apartados especiales, un bolsillo lateral perfecto para tener acceso rápido, bolsillo para calzado en la base con argollas de ventilación, y bolsillo antirrobo en la espalda, además de cable USB. Perfecta para viajes o para el día a día por sus cintas ajustables, adaptándose a todo estilo de vida. Disponible en negro y gris, con logotipo RPET incluido. Presentada individualmente en bolsa reciclada.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20354', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6892, 'Multifunctional backpack made of 300D RPET. Made from recycled and water resistant polyester, with excellent breathability in the back area, facilitating heat dissipation thanks to its padded straps and padded back. The wide shoulder straps are designed to relieve pressure on the shoulders, enabling the backpack to be worn comfortably for long periods of time.Perfect for any situation, and with multiple compartments: 2 front pockets. 3 large central compartments, one of which is a large compartment, a large central pocket for storing clothes and/or accessories with an organiser inside, and a third padded pocket, perfect for carrying a laptop up to 17.3 inches. It also has 3 special compartments, a side pocket perfect for quick access, a shoe pocket on the bottom with ventilation eyelets, and an anti-theft pocket on the back, plus USB cable. Perfect for travel or everyday use with adjustable straps to suit all lifestyles. Available in black and grey, with RPET label included. Presented individually in a recycled bag.', '20354', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6893, 'Trolley fabricado en resistente 600D RPET. Compartimento principal con cierre de cremallera de doble tirador conforme a los estándares TSA -Administración de la Seguridad en el Transporte-, que permite a las autoridades de aduanas inspeccionar el contenido del equipaje sin tener que forzar o romper el cierre, usando una llave especial a disposición de la autoridad.Interior forrado además de organizador en la zona central. Exterior con bolsillo frontal con cierre de cremallera invisible y 2 asas acolchadas de transporte. Mecanismo extensible metálico en negro, con cómoda asa de transporte y 2 ruedas. Logotipo RPET en etiqueta exterior.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20355', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6894, 'Trolley made of durable 600D RPET. Main compartment with TSA (Transportation Security Administration) compliant double pull zip, which allows customs authorities to inspect the contents of the luggage without having to force or break the lock, using a special key available to the authority.Lined interior plus organiser in the centre area. Outside with front pocket with invisible zip closure and 2 padded carry handles. Expandable metal mechanism in black, with comfortable carrying handle and 2 wheels. Outside RPET label.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20355', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6895, 'Práctico set para limpiar calzado en estuche de RPET. Completo paquete para limpiar calzado con dos cepillos de suaves cerdas, calzador, lata con cera y paño para que brille todo el calzado. Presentado en un práctico estuche con cierre de cremallera y elásticos para tener organizados todos los utensilios. Con logotipo RPET en la funda. El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20356', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6896, 'Practical shoe cleaning set in RPET case. With two soft bristle brushes, shoehorn, a wax can and a shoe polishing cloth. Presented in a practical case with zip closure and elastic band holders to keep all tools organised. With RPET logo on the case.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20356', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6897, 'Cinta para maletas ajustable fabricada en resistente poliéster. Con cierre de hebilla ajustable y contraseña de 3 cifras. En discreto color negro y de 200cm de largo. Presentada en una bolsa individual de papel reciclado.', '20359', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6898, 'Luggage strap made of durable polyester. With buckle fastener and 3-digit password lock system. In stylish black color and 2m long. Presented in an individual recycled paper bag.', '20359', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6899, 'Fantástica almohadilla autoinflable en suave poliéster. Almohada portátil con cómodo sistema de auto inflado: desenroscando la boquilla en una de sus esquinas automáticamente empezará a llenarse de aire, con la posibilidad de ajustar la dureza. Fácil de guardar gracias a su funda de RPET, con logotipo incluido.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20360', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6900, 'Fantastic self-inflating pillow in soft polyester. Portable and with convenient self-inflating system: by unscrewing the nozzle in one of its corners it will automatically start to be filled with air, allowing the user to adjust the pressure. Easy to store thanks to its RPET sleeve, with RPET logo included.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20360', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6901, 'Estupendo set de viaje en suave poliéster. El conjunto incluye: Almohadilla ergonómica para cuello. Suave antifaz con cinta ajustable Zapatillas a conjunto. Fácil de guardar en la funda fabricada en RPET. Disponible en elegantes colores, con logotipo RPET en la funda.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20361', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6902, 'Great travel set in soft polyester. Including: Ergonomic neck pad. Soft mask with adjustable strap. Matching pair of slippers. Easy to store inside an RPET pouch and available in stylish colours, with RPET logo on the pouch.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20361', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6903, 'Bolsa portazapatos fabricada en non-woven. Práctica para guardar calzado, con serigrafía inspirada en zapatillas de cordones y práctica ventana transparente para visualizar el interior, con cierre de cordón. Disponible en variados colores diferentes y detalles en blanco.', '20362', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6904, 'Shoe bag made of non-woven. Practical for storing footwear and printed with a classic shoe design. Including a practical transparent window to view the contents, and a drawstring fastening. Available in a variety of of colours and with white details.', '20362', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6905, 'Ligera y práctica báscula digital para equipaje. Con pantalla digital y capacidad de hasta 50kg de peso. Fácil de transportar y de enganchar a maletas, mochilas o bolsas gracias a su práctico gancho. Disponible manual de instrucciones en español e inglés. Alimentación a pilas (1 pila de botón incluida).', '20363', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6906, 'Lightweight and practical luggage scale. With digital display and weighing capacity up to 50kg. Easy to carry and attach to suitcases, backpacks or bags using its practical hook. Instructions manualavailable in English and Spanish. Battery powered (1 button cell battery included).', '20363', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6907, 'Completo adaptador de enchufes, de diseño compacto y portable. Con salidas USB y Tipo C, especialmente indicado para viajes y con adaptadores multi región para USA, Europa, Asia, Reino Unido y Australia. Indicador LED de encendido y presentado individualmente en caja de diseño kraft.', '20364', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6908, 'Complete plug adapter, with a compact and portable design. With USB and Type-C outlets, especially suitable for travel and with multi-region adapters for USA, Europe, Asia, UK and Australia. LED power indicator and individually packaged in a kraft design box.', '20364', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6909, 'Práctico quita pelusas de mano. De uso cómodo, gracias a su forma ergonómica adaptándose a la mano. Se usa pasando la zona metálica por el tejido para dejar la tela suave. Con compartimento en el cabezal para guardar el exceso y poder limpiarlo fácilmente. Cómodo de transportar y alimentado por 2 pilas AA (no incluidas). Presentado en una caja de diseño kraft, con manual español e inglés en el interior.', '20365', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6910, 'Practical hand-held lint remover. Comfortable to use and with an ergonomic shape that adapts to the hand. It is used by rubbing the metal area on the fabric until the fabric is softened. With compartment for storing the removed lint, easy to clean. Portable and powered by 2 AA batteries (not included). Presented in a kraft design box, with Spanish and English manual inside.', '20365', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6911, 'Fantástica plancha de viaje, para quitar arrugar mediante calor seco. De diseño ergonómico en su agarre y tamaño ligero de transportar, con enchufe EU. Presentada en una caja de diseño kraft con instrucciones en español e inglés.', '20366', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6912, 'Fantastic travel iron for dry heat crease removal. Ergonomically designed, lightweight and easy to carry, with EU plug. Presented in a kraft design box with instructions in English and Spanish.', '20366', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6913, 'Paraguas infantil con divertidos diseños de gato y rana. De 78cm de diámetro y fabricado en resistente material poliéster 180T. Apertura automática, con caña y varillas de metal, mango curvo y funda de poliéster a juego.', '20367', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6914, 'Children\'s umbrella with fun cat and frog designs. With a diameter of 78cm and made of resistant 180T polyester material. Automatic opening, with metal pole and ribs, curved handle, and matching polyester cover.', '20367', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6915, 'Set para colorear hecho en cartón reciclado. Divertido conjunto de dibujos y lápices de colores en una práctica carpeta con asas. Incluye:Bloc de 20 hojas con dibujos de naturaleza para colorear.Caja con 6 lápices de madera con minas en multitud de colores.Sacapuntas.Goma de borrar.', '20368', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6916, 'Colouring set made of Recycled cardboard. Including a playful set of drawings and colouring pencils in a practical folder with handles. Contents:20-sheet pad with nature drawings to be coloured in.Box with 6 wooden pencils in a variety of colours.Sharpener.Eraser.', '20368', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6917, 'Original frisbiee personalizable fabricado en caña de trigo. Divertido conjunto de ceras y frisbiee con zona para poder colorear el original dibujo de naturaleza. Presentado en bolsa de papel reciclado. Incluye:Frisbee con dibujo de naturaleza para colorear.Caja de 6 ceras en multitud de colores.', '20369', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6918, 'Original customisable frisbiee made of wheat cane. Fun set of crayons and frisbiee, with an area to colour the original nature drawing. Presented in a Recycled paper bag. Including:Frisbee with nature drawing to colour in.Box of 6 crayons in a variety of colours.', '20369', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6919, 'Set de 6 lápices en caja de cartón natural con semillas. Lápices de madera con cuerpo hexagonal de acabado natural y minas en variada gama de colores. Incluye semillas de petunia en la caja, convirtiéndolo así en cartón plantable al final de su vida útil. Flores en colores surtidos, con instrucciones de plantado en la cubierta trasera.', '20370', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6920, 'Set of 6 pencils in a natural cardboard box with seeds. Wooden pencils with hexagonal body and natural finish, with leads in a wide range of colours. Includes petunia seeds in the box, making it plantable cardboard at the end of its lifespan. Flowers in assorted colours, with planting instructions on the back cover.', '20370', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6921, 'Set de 6 ceras en variada gama de colores, presentadas en caja de cartón natural con semillas. Incluye semillas de petunia en la caja, convirtiéndolo así en cartón plantable al final de su vida útil. Flores en colores surtidos, con instrucciones de plantado en la cubierta trasera.', '20371', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6922, 'Set of 6 crayons in a natural cardboard box with seeds. Includes petunia seeds in the box, making it plantable cardboard at the end of its lifespan. Flowers in assorted colours, with planting instructions on the back cover.', '20371', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6923, 'Set de 6 lápices con plantillas para colorear y sacapuntas, presentado en caja de cartón natural con semillas. Lápices de madera con cuerpo redondo de acabado natural y minas en variada gama de colores. Incluye semillas de petunia en la caja, convirtiéndolo así en cartón plantable al final de su vida útil. Flores en colores surtidos, con instrucciones de plantado en la cubierta trasera.', '20372', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6924, 'Set of 6 pencils with colouring templates and pencil sharpener, presented in a natural cardboard box with seeds. Wooden pencils with round body and natural finish, with leads in a wide range of colours. Includes petunia seeds in the box, making it plantable cardboard at the end of its lifespan. Flowers in assorted colours, with planting instructions on the back cover.', '20372', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6925, 'Divertida diana de tela con pelotas de velcro. Perfecta para todas las edades ofreciendo un objetivo desafiante y seguro. El juego consiste en lanzar a la diana con puntuaciónes las pelotas de distintos colores, que se quedarán fijas gracias a su envoltura de velcro. Fácil de colgar en cualquier sitio y a cualquier altura mediante agujero. Incluye diana de tela flexible y 3 pelotas con velcro. Presentado en bolsa reciclada.', '20373', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6926, 'Fun fabric dartboard with velcro balls. Perfect for all age groups, offering a challenging and safe target. The game consists of throwing the different coloured balls at the target with scores, which will stay in place by the velcro wrapping. Easy to hang anywhere and at different heights by means of a hole. Including a flexible fabric dartboard and 3 balls with velcro. Presented in a Recycled bag.', '20373', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6927, 'Lápiz eterno fabricado en RABS. Reutilizable y con escritura infinita, para usar siempre el mismo lápiz y no generar residuos. Incluye goma de borrar en la parte superior del lápiz y capucha para proteger la punta. Con logotipo RABS en un extremo de la caña. Disponible en una amplia gama de colores.El RABS es un plástico altamente reciclable de gran resistencia y con multitud de aplicaciones que lo convierte en una solución eco-friendly. con certificación RCS, que garantiza el origen reciclado del mismo.', '20374', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6928, 'Eternal pencil made of RABS. Reusable and with infinite writing, to always use the same pencil and not generate waste. Including eraser on top of the pencil and a cap to protect the tip. With RABS logo on one end of the barrel and available in a wide range of colours.RABS is a highly recyclable, resistant plastic with a multitude of applications that makes it an eco-friendly solution. With RCS certification, guaranteeing its recycled origin.', '20374', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6929, 'Lápiz eterno fabricado en resistente ABS. Reutilizable y con escritura infinita, para usar siempre el mismo lápiz y no generar residuos. Incluye goma de borrar en la parte superior del lápiz, capucha para proteger la punta, con estilo elegante combinando elementos de colores con elementos en plata. Disponible en una amplia gama de colores.', '20375', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6930, 'Eternal pencil made of durable ABS. Reusable and with infinite writing, to always use the same pencil and not generate waste. Including eraser on top of the pencil, and a cap to protect the tip. Elegant style, combining coloured elements with silver elements. Available in a wide range of colours.', '20375', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6931, 'Original sacapuntas fabricado en caña de trigo y bambú. Fácil y cómodo de usar con dos sacapuntas de diá metros diferentes; hechos en caña de trigo para afilar lápices diferentes. Además, tiene contenedor hecho en bambú para guardar la viruta. La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20376', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6932, 'Original sharpener made of wheat straw and bamboo. Easy and handy to use, with two pencil sharpeners in different diameters. In addition, it has a bamboo container to store the shavings.Wheat straw encourages the use of natural raw materials, thus reducing pollutant emissions.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20376', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6933, 'Original sacapuntas fabricado en caña de trigo. Práctico sacapuntas de bolsillo de inspiración natural por su forma de piedra pulida con práctica tapadera fabricada en fibra de trigo, cómodo de usar y guardar. La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20377', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6934, 'Original pencil sharpener made of wheat straw. Practical pocket sharpener inspired by nature due to its polished stone shape. With a practical lid made of wheat straw, it is easy to use and store.Wheat straw encourages the use of natural raw materials, thus reducing pollutant emissions.', '20377', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6935, 'Practica regla de 30cm fabricada en madera. Herramienta perfecta para mediciones precisas, combinando diseño ecológico y practicidad.', '20378', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6936, 'Useful 30cm ruler made of wood. Perfect tool for precise measurements, combining eco-friendly design and practicality.', '20378', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6937, 'Sostenible regla de 30cm fabricada con caña de trigo. Herramienta en perfecta combinación de funcionalidad y ecología, siendo una elección respetuosa con el medio.La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20379', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6938, 'Sustainable 30cm ruler made of wheat straw. A tool that is a perfect combination of functionality and ecology, making it an environmentally friendly choice.Wheat straw encourages the use of natural raw materials, thus reducing polluting emissions.', '20379', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6939, 'Set de 5 piezas fabricado en caña de trigo. Incluye 2 lápices de madera con mina negra y cuerpo cilíndrico blanco; goma y sacapuntas. Presentado en estuche/regla de 20 cm/8 pulgadas, con compartimentos para los distintos elementos del set. Presentado individualmente en una bolsa de papel blanca. La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20380', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6940, '5-piece set made of wheat straw. Including 2 wooden pencils with black lead and white cylindrical body; eraser, and sharpener. Presented in a case with ruler of 20 cm/8 inches, with compartments for the different elements of the set. Individually packed in a white paper bag.Wheat straw promotes the use of natural raw materials, thus reducing pollutant emissions.', '20380', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6941, 'Ingenioso silbato multiusos con luz integrada en el cuerpo. Fabricado en resistente ABS, con 6 luces COB y varias posiciones de iluminación: fija con dos niveles de intensidad e intermitente para una mayor visibilidad. Cordón para un cómodo transporte y está alimentado por una pila de botón (incluida). Presentado en caja de diseño kraft.', '20381', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6942, 'Multi-purpose whistle with integrated light in the body. Made of robust ABS, with 6 COB lights and several lighting positions: steady with two levels of intensity; and flashing for a greater visibility. Lanyard for convenient carrying and powered by a button cell battery (included). Presented in a kraft design box.', '20381', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6943, 'Luz de seguridad de alta visibilidad. Dispone de 3 luces LEDs, con posición de iluminación fija -dos niveles de intensidad-, e intermitente. Con mosquetón giratorio y disponible en colores vivos. Alimentada por pila de botón (incluida) y presentada individualmente en caja de diseño kraft.', '20382', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6944, 'High visibility safety light. Equipped with 3 LED lights, with fixed lighting position (two levels of intensity) and flashing light. With swivel carabiner and available in bright colours. Powered by button cell battery (included) and presented individually in a kraft design box.', '20382', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6945, 'Juego de habilidad sudoku fabricado en suave madera. Incluye 16 fichas de madera numeradas, en estuche de madera con tapa-tablero. Con reglas del juego en español e inglés. Presentado individualmente en caja de diseño kraft.', '20383', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6946, 'Sudoku skill game made of soft wood. Includes 16 numbered wooden pieces, in a wooden case with cover-board. Including rules of the game in English and Spanish. Presented individually in a kraft design box.', '20383', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6947, 'Tableta LCD para escritura en resistente material, con fijación magnética y soporte para el lápiz. Pantalla LCD de 4,5 pulgadas, con lápiz incluido, botón de borrado, bloqueo anti borrado y pila de botón incluida. Presentado en caja de diseño kraft.', '20384', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6948, 'LCD writing tablet made of resistant material, with magnetic fixing and pen holder. 4.5 inches LCD screen, including stylus, erase button and erase lock. Button battery included and presented in kraft design box.', '20384', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6949, 'Divertido yoyó en resistente fibra de bambú, para retumblererar los juegos clásicos más divertidos. Disponible en varios y llamativos colores. Con hilo rizado de algodón en color blanco de 100 cm.La fibra de bambú fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20385', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6950, 'Fun yo-yo made of resistant bamboo fibre, to recall the most entertaining classic games. Available in a variety of bright colours. With a 100 cm, cotton braided yarn in white.The bamboo fibre promotes the use of natural raw materials, thus reducing pollutant emissions.', '20385', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6951, 'Set de escritura y colores con cuaderno. Divertido conjunto de artículos fabricados en madera compuesto por lápices con minas en varios colores, sacapuntas, bolígrafo fabricado en cartón reciclado y goma. Presentado en una práctica bolsa de algodón 100%.', '20386', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6952, 'Writing and colouring set with notebook. Fun set of items made of wood and composed of pencils with leads in different colours, sharpener, ball pen made of Recycled cardboard and eraser. Presented in a practical and practical 100% cotton bag.', '20386', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6953, 'Set de escritura fabricado en caña de trigo. Práctico conjunto compuesto por: - Lápiz hecho en madera- Bolígrafo fabricado en cartón reciclado y caña de trigo- Regla hecha con caña de trigo- Sacapuntas de madera- Goma- Estuche hecho en caña de trigo. La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20387', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6954, 'Writing set made of wheat straw. Practical kit consisting of: - Pencil made of wood- Ball pen made of Recycled cardboard and wheat straw.- Ruler made of wheat straw- Wooden sharpener- RubberPencil case made of wheat straw. Wheat straw promotes the use of natural raw materials, thus reducing pollutant emissions.', '20387', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6955, 'Original atril con pizarra hecha en madera, con tiza incluida. Presentado individualmente en caja de diseño kraft.', '20388', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6956, 'Genuine lectern with chalkboard made in wood, chalk included. Presented individually in a kraft design box.', '20388', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6957, 'Bidón fabricado en RPET de 350 ml de capacidad, libre de BPA. Tapón a rosca en acero inox, con cordón de transporte. Disponible en variada gama de colores. Presentado en una bolsa de papel reciclado.Elaborado a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20389', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6958, 'Bottle made of RPET and with 350 ml capacity, BPA free. Stainless steel screw cap, with carrying strap. Available in a wide range of colours and presented in a Recycled paper bag.', '20389', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6959, 'Set de cepillo de dientes y reloj de arena fabricado en bambú y caña de trigo. Incluye cepillo con mango ergonómico hecho en suave bambú con capucha protectora. Además, incluye un reloj de arena de 2 minutos de duración fabricado en caña de trigo. Presentado un práctico estuche hecho de algodón transpirable. La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20391', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6960, 'Toothbrush and sand timer set made of bamboo and wheat straw. Including toothbrush with ergonomic handle made in soft bamboo, with protective hood. Also including a 2-minute sand timer made of wheat straw. Presented in a handy case made of breathable cotton. Wheat straw promotes the use of natural raw materials, thus reducing pollutant emissions.', '20391', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6961, 'Cepillo de dientes fabricado en bambú. Para los más pequeños de casa, con cerdas y puntas en divertidos colores. Presentado individualmente en caja kraft. Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20392', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6962, 'Toothbrush made of bamboo. Designed for kids, with colourful bristles and tips. Presented individually in a kraft box.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20392', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6963, 'Mochila de cuerdas en material 100% algodón reciclado de 120 g/m2. Cuerdas autocierre en algodón, con logotipo eco en etiqueta lateral y disponible en variada gama de colores nature. Resistencia hasta 4kg de peso.', '20393', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6964, 'Drawstring backpack in 100% Recycled cotton material 120 g/m2. Self closing strings in cotton, with eco label on one side and available in a wide range of nature colours. Resistance up to 4kg in weight.', '20393', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6965, 'Juego de habilidad fabricado en madera. Contiene 12 piezas poligonales fabricadas en suave madera y pintadas en divertidos colores. Permite formar diversas figuras gracias a las diferentes formas de las piezas, pudiendo guardarlas cómodamente en su base fabricada también en madera.', '20394', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6966, 'Skill game made of wood. Contains 12 polygonal pieces made of soft wood and painted in fun colours. The different shapes of the pieces enable different figures to be created, which can be conveniently stored in its base, which is also made of wood.', '20394', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6967, 'Juego de habilidad de 6 piezas fabricado en suave bambú. Un divertido rompecabezas perfecto para el entretenimiento: hay que ensamblar las piezas en una combinación única que nos mostrará la figura buscada. Presentado individualmente en una caja de diseño kraft.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20395', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6968, '6-piece skill game made of soft bamboo. A fun puzzle perfect for entertainment: the pieces are assembled in a unique combination that will reveal the target figure. Presented individually in a kraft design box.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20395', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6969, 'Juego de habilidad de 2 piezas fabricado en madera. Divertido rompecabezas de dos piezas unidas por un cordón de madera, cuyo objetivo es descubrir la forma de separar ambas piezas. Presentado individualmente en una caja de diseño kraft.', '20396', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6970, '2-piece skill game made of wood. Fun puzzle of two pieces joined by a wooden string, with the objective of finding the way to separate the two pieces. Presented individually in a kraft design box.', '20396', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6971, 'Juego de habilidad fabricado en madera. Emocionante juego sudoku con motivos ecológicos para todas las edades con tablero y 36 fichas (6 por diseño). Con práctica bolsita de algodón para guardar todas las fichas e instrucciones incluidas (español e ingles).', '20397', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6972, 'Skill game made of wood. Exciting sudoku game with ecological motifs for all ages with board and 36 pieces (6 per design). With practical cotton bag to store all the pieces, and instructions manual included (English and Spanish).', '20397', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6973, 'Divertido juego de mesa fabricado en caña de trigo. Compuesto por un tablero vertical y 42 piezas circulares (21 rojas y 21 azules). Emoción para todas las edades introduciendo las fichas por las ranuras para conseguir 4 fichas en 1 misma fila. Fácil de montar por su tablero móvil pudiendo desplegar o plegar. Presentado en caja de diseño kraft.', '20398', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6974, 'Fun board game made of wheat straw. Composed of a vertical board and 42 circular pieces (21 red and 21 blue). Exciting for all ages by inserting the pieces through the slots to get 4 pieces in 1 row. Easy to assemble thanks to its mobile board that can be unfolded or folded. Presented in a kraft design box.', '20398', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6975, 'Juego de habilidad fabricado en madera, compuesto por 9 piezas y un tablero. El desafío consiste en deslizar las 8 piezas con flechas por el tablero para ordenarlas apuntando a la ficha con un punto, ofreciendo destreza y estrategia. Presentado individualmente en una caja de diseño kraft.', '20399', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6976, 'Skill game made of wood, consisting of 9 pieces and a board. The challenge involves sliding the 8 pieces with arrows across the board and arranging them pointing to the piece marked with a dot, combining skill and strategy. Presented individually in a kraft design box.', '20399', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6977, 'Tierno y suave oso de peluche con sudadera personalizable. Un divertido regalo con el que sorprender.', '20400', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6978, 'Soft and cuddly teddy bear with customisable sweatshirt. A fun gift to surprise with.', '20400', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6979, 'Encantadora hucha con la silueta de un cerdito combinando diseño y funcionalidad, para hacer del ahorro una experiencia alegre, viendo por su ventana de metacrilato como suben los ingresos. Presentado individualmente en caja de diseño kraft.', '20401', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6980, 'Lovely piggy bank with the silhouette of a little pig, combining design and functionality and turning saving into a joyful experience, by watching through its methacrylate window how the savings rise. Presented individually in a kraft design box.', '20401', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6981, 'Divertido juego de habilidad fabricado en madera y cuyo objetivo es superar un desafiante laberinto con 15 obstáculos, evitando que la bola metálica interior caiga en los agujeros. Presentado individualmente caja de diseño kraft.', '20402', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6982, 'Fun skill game made of wood, with the objective to overcome a challenging maze with 15 obstacles, and avoiding the metal ball inside to fall into the holes. Individually presented in a kraft design box.', '20402', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6983, 'Entretenido juego de parchis fabricado en madera. Contiene un tablero, 16 fichas de 4 colores diferentes y un dado. Para participar entre 2 y 4 jugadores. Con práctica bolsita de algodón para guardar las fichas. Presentado individualmente en caja de diseño kraft.', '20403', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6984, 'Entertaining parcheesi game made of wood. Contains a board, 16 counters in 4 different colours and a dice. Suitable for 2 to 4 players. With handy cotton bag for storing the pieces. Presented individually in a kraft design box.', '20403', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6985, 'Juego de puntería fabricado en madera. Consiste en colar los aros de cuerda en la base con palos del mismo color (cada color tiene una puntuación diferente). Compacto y cómodo de montar, incluye 12 piezas. Con instrucciones en español e inglés. Presentado individualmente en una caja de diseño kraft.', '20404', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6986, 'Aiming game made of wood. Involves inserting the rope rings into the base with sticks in the same colour (each colour scores differently). Compact and easy to assemble, including 12 pieces. With instructions in English and Spanish. Presented individually in a kraft design box.', '20404', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6987, 'Divertido juego de mesa fabricado en madera e ideal para los más competitivos. El juego consiste en lanzar las fichas desde cada campo al capo contrario usando el elástico. Gana el que más fichas cuele por la ranura del oponente. Tablero con 5 fichas blancas y 5 negras, presentado en una caja individual de diseño kraft.', '20405', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6988, 'Fun board game made of wood and ideal for the most competitive players. The game consists of launching the pieces to the opponent\'s field using the elastic band. The winner is the player with most tokens inserted into the opponent\'s slot. Including board, 5 white pieces, and 5 black pieces. Presented in an individual kraft design box.', '20405', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6989, 'Set de 4 lápices en caja de cartón reciclada con ventana. Lápices de madera con cuerpo cilíndrico en variados colores fluor y minas en divertidos colores.', '20406', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6990, 'Set of 4 pencils in Recycled cardboard box with window. Wooden pencils with cylindrical body in various fluorescent colours and leads in fun colours.', '20406', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6991, 'Completo maletín para colorear fabricado en madera, con cómoda asa de transporte. Contenido: - 12 rotuladores- 12 lápices- 12 ceras crayón - 12 ceras al aceite - Placa de acuarelas con pincel y paleta - Sacapuntas- Goma- Tarro de pegamento- Regla', '20407', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6992, 'Complete colouring case made of wood, with convenient carrying handle. Contains: - 12 markers- 12 pencils- 12 crayons - 12 oil crayons - Watercolour plate with brush and palette - Pencil sharpener- Eraser- Glue pot- Ruler', '20407', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6993, 'Fantástico set de lápices y bloc hecho en cartón reciclado. Contiene estuche con ventana de 8 lápices con cuerpo hexagonal de variados colores y un bloc de notas de anillas con 6 hojas de motivos naturales y 18 páginas en blanco.', '20408', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6994, 'Great pencils and notebook set made of Recycled cardboard. Window box with 8 pencils and hexagonal shape in different colours. Including a ring notebook with 6 sheets of natural motifs and 18 blank pages.', '20408', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6995, 'Divertido silbato con dos modalidades de sonido distintas. Fabricado en suave madera, de diseño artesanal con un toque natural. Presentado individualmente en bolsa reciclada.', '20409', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6996, 'Cool whistle with two different sound modes. Made of soft wood, handcrafted design with a natural touch. Presented individually in a recycled bag.', '20409', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6997, 'Tierno doudou con lindo diseño de elefante blanco. Con nudos en cada una de las 3 esquinas para estimular el sentido del tacto del bebé, facilitando la relajación. Fabricado en una única pieza de tela, para evitar que se separe en trozos más pequeños.', '20410', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6998, 'Tender doudou with cute white elephant design. With knots in each of the 3 corners to stimulate the baby\'s touch, favouring relaxation. Made from a single piece of fabric to avoid splitting into smaller pieces.', '20410', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (6999, 'Tierno doudou con lindo diseño de osito blanco. Fabricado en una única pieza de tela, para evitar que se separe en trozos más pequeños. Con bordes reforzados en tela bicolor y práctica asa de transporte.', '20411', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7000, 'Tender doudou with cute teddy bear design. With knots in each of the 3 corners to stimulate the baby\'s touch, favouring relaxation. Made from a single piece of fabric to avoid splitting into smaller pieces.', '20411', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7001, 'Práctico babero infantil confeccionado en algodón, con cierre de velcro para un mejor ajuste de color blanco.', '20412', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7002, 'Practical children\'s bib made of cotton, with velcro fastening for a better fit. In white color.', '20412', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7003, 'Parasol en aluminio con burbuja en ambas caras y de tamaño 130x70 cm. Acabado plateado metalizado, con original diseño sin ribetes. Fácil y rápido de plegar, con cierre de corchete. Presentación desplegado en plancha para una óptima impresión, con bolsas aparte.', '20413', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7004, 'Aluminium sunshade with bubble on both sides and size 130x70 cm. Metallic silver finish, with original design without piping. Easily and quickly foldable, with button closure. Presented unfolded flat for an optimal printing, with separated bags.', '20413', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7005, 'Resistente parasol de aluminio en una cara, diseñado para situarlo en el exterior de la luna delantera y como protección ante fenómenos adversos como la nieve. Incluye dos solapas laterales que se ajustan entre el marco y las puertas del vehículo, proporcionando un aislamiento adicional a la luna delantera. Enrollable y con gomas para un fácil almacenamiento.Presentado en bolsa individual reciclada.', '20414', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7006, 'Durable single-sided Aluminium sunshade, designed to be positioned on the outside of the front window and as protection against adverse weather conditions like snow.. Including two side flaps to fit between the frame and the vehicle doors, providing additional insulation to the front window. Roll-up and with elastic bands for an easy storage, and presented in an individual recycled bag.', '20414', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7007, 'Resistente soporte fabricado en ABS. Fácil instalación mediante el gancho abierto diseñado para colocarse en el reposacabezas, con soporte para dispositivos móviles y dos ganchos adicionales para bolsas. Presentado en bolsa individual.', '20415', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7008, 'Robust holder made of ABS. Easy installation using the open hook designed to be placed on the headrest, with holder for mobile devices and two additional hooks for bags. Presented in an individual bag.', '20415', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7009, 'Práctico rascador de hielo fabricado en bambú. Equipado con dientes de sierra troquelados para abordar el hielo más resistente y accesorio limpiador. Presentado individualmente en bolsa de papel kraft.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20416', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7010, 'Practical ice scraper made of bamboo. Equipped with serrated teeth to tackle the toughest ice and inluding a cleaning accessory. Presented individually in a kraft paper bag.Materials such as bamboo encourage the use of natural raw materials, reducing pollutant emissions. It comes from a highly resistant and versatile plant with rapid growth and regeneration, making of it an excellent alternative to traditional wood.', '20416', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7011, 'Práctico rascador de hielo fabricado en bambú y caña de trigo. Con forma de pala, su mango está fabricado en bambú y la zona de rascado está hecha de caña de trigo, con parte limpiadores y sierra integrada. Logo ditintivo en el mango y presentado individualmente en bolsa de papel kraft.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20417', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7012, 'Practical ice scraper made of bamboo and wheat straw. Shovel shaped, with bamboo handle and scratching area made of bamboo and wheat straw, with integrated wipers and saw. Logo on the handle and presented individually in a kraft paper bag.Materials such as bamboo encourage the use of natural raw materials, reducing pollutant emissions. It comes from a highly resistant and versatile plant with rapid growth and regeneration, making of it an excellent alternative to traditional wood.Wheat straw promotes the use of natural raw materials, thus reducing pollutant emissions.', '20417', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7013, 'Práctico llavero rascador de hielo fabricado en caña de trigo. Equipado con dientes de sierra para abordar el hielo más resistente y accesorio limpiador. Logotipo caña de trigo en la esquina superior. Presentado individualmente en bolsa de papel kraft.La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20418', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7014, 'Practical keyring with ice scraper, made of wheat straw. Equipped with saw teeth for tackling the toughest ice and including a cleaning accessory. Wheat straw logo on the top corner and presented individually in a kraft paper bag.Wheat straw promotes the use of natural raw materials, thus reducing pollutant emissions.', '20418', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7015, 'Práctico llavero martillo de emergencia, con función rompecristales y cuchilla cortacinturones. Anilla metálica cromada. Presentado en caja individual kraft.', '20420', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7016, 'Practical key ring with emergency hammer, window breaker function and belt cutter blade. Presented in an individual kraft box.', '20420', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7017, 'Versátil martillo de emergencia. Cuenta con tres funciones: una potente linterna con 6 luces COB alimentadas por pilas de botón (incluidas), una punta metálica rompecristales y una cuchilla cortacinturones. Zona imantada en parte superior y tira para una mejor sujeción. Presentado en caja individual kraft.', '20421', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7018, 'Versatile emergency hammer with three functions: a powerful torch with 6 COB lights powered by button batteries (included), a metal glass breaker tip and a belt cutter blade. With a strap for a better grip, and magnetised area at the top. Presented in an individual kraft box.', '20421', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7019, 'Moderno ambientador para vehículos fabricado en bambú y silicona, con aroma a limón para rejilla de automóvil. Presentado en bolsa de papel kraft. Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20422', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7020, 'Modern air freshener for vehicles. Made of bamboo and silicone, with lemon scent and suitable for car grille. Presented in a kraft paper bag.Materials such as bamboo encourage the use of natural raw materials, reducing pollutant emissions. It comes from a highly resistant and versatile plant with rapid growth and regeneration, making of it an excellent alternative to traditional wood.Wheat straw promotes the use of natural raw materials, thus reducing pollutant emissions.', '20422', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7021, 'Ingenioso soporte para smartphone de diseño minimalista y especialmente diseñado para smartphones tamaño XXL. Con sistema de fijación universal que se adapta con el propio peso del teléfono, ajustando los enganches de agarre una vez se sitúa el dispositivo en el soporte. Para rejilla de ventilación del automóvil y con frontal especialmente diseñado para marcaje. Presentado en caja de diseño kraft.', '20423', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7022, 'Ingenious smartphone holder with minimalist design and specially designed for XXL smartphones. With universal fixing system that adapts to the weight of the phone, adjusting the grip hooks once the device is placed in the holder. For car air vents and with a specially designed front piece for branding. Presented in a kraft design box.', '20423', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7023, 'Soporte ajustable para smartphone de diseño minimalista, con base rotatoria 360º y de elegante acabado en color negro con detalles en bambú. Con sistema de fijación en rejilla de ventilación del automóvil y amplia superficie de marcaje, diseñado especialmente para tampografía. Presentado en caja de diseño kraft. Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20424', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7024, 'Soporte ajustable para smartphone de diseño minimalista, con base rotatoria 360º y de elegante acabado en color negro con detalles en bambú. Con sistema de fijación en rejilla de ventilación del automóvil y amplia superficie de marcaje, diseñado especialmente para tampografía. Presentado en caja de diseño kraft. Materials such as bamboo encourage the use of natural raw materials, reducing pollutant emissions. It comes from a highly resistant and versatile plant with rapid growth and regeneration, making of it an excellent alternative to traditional wood.', '20424', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7025, 'Ingenioso soporte para smartphone de diseño minimalista y especialmente diseñado para smartphones tamaño XXL. Elegante diseño negro con detalles en bambú, con sistema de fijación universal que se adapta por el propio peso del teléfono, ajustando las pinzas de agarre una vez que se sitúa el dispositivo en el soporte, con base rotatoria 360º. Para rejilla de ventilación del automóvil y con frontal especialmente diseñado para marcaje. Presentado en caja de diseño kraft. Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20425', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7026, 'Ingenious smartphone holder with minimalist design and specially designed for XXL smartphones. Elegant black design with bamboo details, with universal fastening system that adapts to the weight of the phone itself, adjusting the gripping clips once the device is placed in the holder. With 360º rotating base and presented in a kraft design box.Materials such as bamboo encourage the use of natural raw materials, reducing pollutant emissions. It comes from a highly resistant and versatile plant with rapid growth and regeneration, making of it an excellent alternative to traditional wood.', '20425', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7027, 'Funda limpiadora para pelotas de golf fabricada en soft shell. Este práctico accesorio cuenta con un paño en su interior para limpiar las pelotas de golf de manera cómoda. Además, incluye un mosquetón giratorio que permite engancharlo fácilmente.', '20426', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7028, 'Cleaning bag for golf balls, made in soft shell. This practical accessory has a cloth inside for convenient cleaning of golf balls. In addition, it includes a swivel carabiner for easy attachment.', '20426', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7029, 'Toalla de golf fabricada en RPET, absorbente y suave con dimensiones de 40x60 cm. En el centro, incorpora una argolla reforzada y un mosquetón de acople, ambos elaborados en acero inox. Con logotipo RPET en etiqueta.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20427', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7030, 'Golf towel made of RPET, absorbent and soft with dimensions of 40x60 cm. In the centre, it includes a reinforced ring and a carabiner, both made in stainless steel. With RPET label.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20427', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7031, 'Toalla absorbente fabricada en RPET de 160g/m2. Tamaño 50x100 cm y presentada en funda individual de redecilla RPET, con parte trasera lisa. Disponible en variada gama de colores, con logotipo RPET en etiqueta.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20428', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7032, 'Absorbent towel made of RPET of 160g/m2. Size 50x100 cm and presented in an individual RPET mesh bag. Available in a wide range of colours, with RPET label.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20428', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7033, 'Práctica bolsa para balones fabricada en RPET. Cuenta con un compartimento central que permite guardar una pelota mediante un cierre de cremallera, siendo fácil de transportar tanto por el asa de mano como por la cinta para hombro. Además, dispone de dos bolsillos laterales de rejilla. Con logotipo RPET en etiqueta.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20429', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7034, 'Useful bag for balls, made in RPET. With a central compartment for storing a ball and with zipper closure. Very easy to carry using the handles or the shoulder strap. It also has two side mesh pockets and an RPET label.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20429', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7035, 'Práctica muñequera en suave poliéster RPET. Complemento perfecto para guardar pequeños objetos gracias a su bolsillo con cierre de cremallera negra. Disponible en variada gama de colores, con etiqueta distintiva RPET.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20430', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7036, 'Practical wristband in soft RPET polyester. Perfect accessory for storing small items thanks to its pocket with black zip closure. Available in a wide range of colours, with RPET label.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20430', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7037, 'Kit de reparación para bicicleta con 15 accesorios. Fácil de llevar gracias a su reducido tamaño, permitiéndote realizar reparaciones de pinchazos en cualquier lugar. Incluye:2 llaves de desmontaje Una llave para tuercas Tubo de pegamento Cera para marcar Dos tubos de goma Lija de metal Dos tapones 5 parches', '20431', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7038, 'Bicycle repair kit with 15 accessories. Easy to carry and compact in size. Including:2 tire removersOne spanner Tube of glue Marking wax Two rubber tubes Metal sandpaper Two caps 5 patches', '20431', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7039, 'Chaleco reflectante homologado según normativa CE. Muy cómodo y fácil de usar, gracias a sus 4 hebillas ajustables y su cierre de hebilla. Disponible en color amarillo fluorescente, con bandas reflectantes y en talla única para adulto.', '20432', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7040, 'Reflective waistcoat approved according to CE regulations. Very comfortable and easy to use, thanks to its 4 adjustable buckles and buckle closure. Available in fluorescent yellow, with reflective strips. One size fits all for adults.', '20432', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7041, 'Práctica bomba para bicicleta, fabricada en resistente aluminio. De inflado manual y en tamaño reducido, con accesorio de acople válido para cualquier tipo de bicicleta.', '20435', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7042, 'Convenient bicycle pump, made of resistant Aluminium. Manually operated and compact in size; with a fitting accessory suitable for all types of bicycles.', '20435', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7043, 'Luz de seguridad para bicicletas con cinco posiciones de luz (fija blanca o roja, intermitente rojo rápido o lento, mixto con luz blanca y roja). Recargable gracias a su batería de 350 mAh de capacidad, con cable de carga incluido. Con accesorio de acople válido para cualquier bicicleta y presentada en caja de diseño kraft.', '20436', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7044, 'Bicycle safety light with five light positions: fixed white or red; flashing red (fast or slow); combined with white and red light. Rechargeable thanks to its 350 mAh battery capacity, with charging cable included. With fitting accessory suitable for all bikes and presented in a kraft design box.', '20436', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7045, 'Luz de seguridad para bicicletas con carga solar. Ofrece tres modos de iluminación: uno fijo con intensidad media o alta, e intermitente. Recargable gracias a su batería de 500 mAh de capacidad, con cable de carga incluido. Con accesorio de acople válido para cualquier bicicleta y presentada en caja de diseño kraft.', '20437', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7046, 'Solar-charged bicycle safety light. Three lighting modes: a fixed mode with medium or high intensity, and a flashing mode. Rechargeable thanks to its 500 mAh battery capacity, with charging cable included. Fitting accessory suitable for all bikes and presented in a kraft design box.', '20437', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7047, 'Kit de luces de seguridad para bicicleta fabricadas en silicona y ABS. Este conjunto consta de dos piezas: una luz blanca para el frontal y otra luz roja para la parte trasera, mejorando la visibilidad. Equipadas con tres potentes LEDs, que ofrecen 4 modos de iluminación: fijo de intensidad alta o media, e intermitente rápido o lento. Recargable gracias a su batería de 650 mAh de capacidad, con cable de carga incluido. Con accesorios de acople válidos para cualquier bicicleta y presentado en caja de diseño kraft.', '20438', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7048, 'Bicycle safety light kit, made of silicone and ABS. This set contains two pieces: a white front light and a rear red light to improve visibility. Equipped with three powerful LEDs, offering 4 lighting modes: steady high or medium intensity; and fast or slow flashing. Rechargeable thanks to its 650 mAh battery capacity, with charging cable included. Fitting accessories valid for any bicycle and presented in a kraft design box.', '20438', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7049, 'Funda táctil para smartphone diseñada para bicicletas. Fácil de enganchar a cualquier manillar mediante un tornillo de montaje sencillo y gomas de presión. La funda cuenta con un clip de enganche y viene presentada en una bolsa individual reciclada.', '20439', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7050, 'Touchscreen smartphone case designed for bicycles. Easy to attach to any handlebar with a simple mounting screw and snap-on rubber bands. The case is equipped with a clip and comes in an individual recycled bag.', '20439', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7051, 'Bolsa bandolera para bicicleta fabricada en resistente RPET 600D. Versátil y con cierre de hebilla, incluye un práctico bolsillo frontal con cremallera. En su parte trasera cuenta con enganches específicos universales para bicicleta, garantizando una mayor comodidad y seguridad en los desplazamientos. Además, dispone de un asa bandolera acolchada y ajustable, para así transportarla con mayor confort cuando se desacopla de la bicicleta. Etiqueta exterior con logotipo RPET. El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20442', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7052, 'Bicycle shoulder bag made of durable 600D RPET. Versatile and with buckle closure, including a handy front pocket with zip. On the back it has specific and universal fittings for bicycles, guaranteeing a greater comfort and safety on the move. It also has a padded and adjustable shoulder strap, so it can be carried comfortably when detached from the bike. Outside RPET label.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20442', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7053, 'Mochila para bicicleta fabricada en resistente RPET 600D. Versátil y con cierre de hebilla, incluye un práctico bolsillo frontal con cremallera. En su parte trasera cuenta con enganches específicos universales para bicicleta, garantizando una mayor comodidad y seguridad en los desplazamientos. Además, dispone de un asas acolchadas y ajustables para los hombros, para así transportarla con mayor confort cuando se desacopla de la bicicleta. Etiqueta exterior con logotipo RPET.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20443', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7054, 'Bike backpack made of durable 600D RPET. Versatile and with buckle closure, including a practical front pocket with zip. On the back it has specific universal fittings for bicycles, guaranteeing a greater comfort and safety on the move. It also has a padded and adjustable shoulder straps, so it can be carried comfortably when detached from the bike. Outside RPET label.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20443', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7055, 'Bolsa para bicicleta impermeable. Complemento perfecto para bicicletas, con acoples para sillín y tija. Con cierre de hebilla y sistema de cierre plegable para una mayor impermeabilidad.', '20444', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7056, 'Waterproof bike bag. Perfect accessory for bicycles, with saddle and seatpost attachments. With buckle closure and folding closure system for greater waterproofn', '20444', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7057, 'Muñequera elástica en alta calidad, fabricada en material técnico 100% algodón. Diseñada para actividades deportivas y de ocio.', '20445', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7058, 'High quality elastic wristband made of 100% technical cotton material. Designed for sports and leisure activities.', '20445', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7059, 'Bidón plegable de 500 ml, práctico y versátil. Su cuerpo flexible de acabado mate permite plegarlo para un almacenamiento cómodo y sencillo. Incluye un dosificador con tapón, y está libre de BPA.', '20446', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7060, 'Practical and versatile 500 ml foldable bottle. Its flexible, matt-finished body folds flat for an easy and convenient storage. Includes a dispenser with cap, BPA free.', '20446', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7061, 'Vaso plegable de 230 ml, práctico y versátil. Su cuerpo flexible de acabado mate permite plegarlo para un almacenamiento cómodo y sencillo. Incluye un asa que facilita su enganche. Además, está libre de BPA.', '20447', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7062, 'Practical and versatile 230 ml foldable tumbler. Its flexible, matt-finished body folds flat for an easy and convenient storage. Including a handle for easy attachment. BPA free.', '20447', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7063, 'Bidón de 800 ml fabricado en resistente RPET y cuerpo de acabado transparente en variada gama de colores. Tapón negro de rosca, con dosificador y dosificador plegable. Con cinta de transporte a juego y mosquetón metálico para un cómodo transporte. Disponible en variada gama de colores.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20448', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7064, '800 ml bottle made of resistant RPET with transparent body in a wide range of colours. Black screw cap, with a dispenser and a foldable dispenser. With matching carrying strap and metal carabiner for easy transport. Available in a wide range of colours.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20448', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7065, 'Bidón de 840 ml fabricado en resistente PET, con un diseño ergonómico que se adapta cómodamente a la mano. Cuenta con un tapón a rosca que incluye un dosificador, asegurando un sellado perfecto. Disponible en una variada gama de colores y presentado de manera individual en bolsa de papel.', '20449', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7066, '840 ml bottle made of durable PET, with an ergonomic design that fits comfortably in the hand. It has a screw cap that includes a dispenser, ensuring a perfect seal. Available in a wide range of colours and presented individually in a paper bag.', '20449', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7067, 'Guantes deportivos fabricados en suave poliéster y licra de neopreno, cuya combinación de tejidos crea una segunda piel que se adapta a la mano. cuentan con un cierre en la muñeca mediante velcro. Estos guantes son cómodos para cualquier disciplina gracias a su alta transpirabilidad, que se logra mediante el diseño de dedos al aire y los disipadores de calor en la parte superior, que además presentan un acabado rugoso para un óptimo agarre. Disponibles en color negro y con costuras en gris, en tallas S-M y L-XL. Presentados en una bolsa reciclada.', '20450', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7068, 'Sports gloves made of soft polyester and neoprene lycra, a combination of fabrics that creates a second skin that adapts to the hand, with a velcro closure at the wrist. These gloves are comfortable for any discipline thanks to their excellent breathability, which is achieved through the open finger design and the heat dissipators on the upper part, which also have a rough finish for an optimal grip. Available in black colour with grey stitching, in sizes S-M and L-XL. Presented in a recycled bag.', '20450', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7069, 'Balón de futbol de diseño retro fabricado en resistente polipiel. Con diseño inspirado en los antiguos balones de cuero y válvula reforzada con un cordón en el mismo material. Ideal para exposición o para disfrutar del juego. Presentación desinflado y en bolsa individual reciclada.', '20451', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7070, 'Retro design football made of resistant PU leather. Design inspired by the old leather footballs and reinforced in the valve with a cord in the same material. Ideal for display or to enjoy the game. Presented deflated and in an individual recycled bag.', '20451', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7071, 'Práctica mochila confeccionada en poliéster 600D RPET. Cuenta con asas acolchadas que facilitan su transporte, así como con tres bolsillos distintos para una organización óptima: un bolsillo principal de gran capacidad, con cierre de doble cremallera; un bolsillo frontal de acceso rápido con cierre de cremallera; y un bolsillo de cremallera con rejilla, ideal para el calzado o balones, proporcionando una buena transpiración. Disponible en una variada gama de colores, con cremalleras en color gris.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20453', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7072, 'Practical backpack made of 600D RPET polyester. It has padded handles for easy carrying, as well as three different pockets for optimal storage: a large capacity main pocket with double zip closure; a quick access front pocket with zip closure; and a zipped pocket with mesh, ideal for shoes or balls, providing breathability. Available in a wide range of colours, with grey zips.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20453', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7073, 'Bolso confeccionado en 600D RPET. Presenta tres compartimentos funcionales: un compartimento principal de gran capacidad con cierre de cremallera, una base con cierre de doble cremallera ideal para el calzado, y un bolsillo lateral de fácil acceso para objetos pequeños, con cierre de cremallera. Se encuentra disponible en una variada y viva gama de colores. Presentada individualmente en bolsa reciclada. El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20454', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7074, 'Bag made of 600D RPET. It features three functional compartments: a large main compartment with zippered closure, a double zippered bottom compartment ideal for footwear, and an easy-access side pocket for small items with zippered closure. Available in a variety of vibrant colours and presented individually in a recycled bag.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20454', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7075, 'Brújula de bolsillo elaborada en resistente material ABS. Con tapa de cierre, dial en negro y los puntos cardinales en verde para facilitar la orientación. Presentada en caja individual.', '20455', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7076, 'Pocket compass made of durable ABS material. With closing lid, black dial and green cardinal points for easy orientation. Presented in an individual box.', '20455', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7077, 'Taza doble pared en Acero Inox Reciclado de 300 ml. Con asa en forma de mosquetón para fácil transporte y cuerpo en variados colores mate. Presentada en caja de diseño kraft.El acero es uno de los materiales más demandados y que puede encontrarse casi en todas partes, desde productos de uso diario a grandes construcciones. Con innumerables propiedades, entre las que cabe destacar la durabilidad y su alto grado de reutilización, convirtiéndolo en una alternativa muy valorada en soluciones de sostenibilidad. Debidamente tratado, permite obtener nuevos recursos de infinita utilización.', '20456', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7078, 'Recycled Stainless Steel double wall mug of 300 ml capacity. With carabiner handle for easy carrying and body in various matt colours. Presented in a kraft design box.Steel is one of the most demanded materials and can be found almost everywhere, from everyday products to major constructions. With countless properties, including durability and a high level of reusability, it is a much valued alternative for sustainable solutions. Properly treated, new resources of infinite use can be obtained.', '20456', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7079, 'Taza doble pared en acero inox de 145 ml. De diseño brillante con tapadera negra de presión con sellado óptimo por su aro de silicona, tiene un práctico enganche con mosquetón para un fácil transporte. Presentada en caja individual de diseño kraft.', '20457', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7080, '145 ml stainless steel double wall mug. Shiny design, with black press-on lid and optimal sealing due to the silicone ring. including a practical carabiner for easy transport and is presented in an individual kraft design box.', '20457', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7081, 'Completa multiherramienta con 16 funciones en acero inox. Incluye: mosquetón para transportarlo fácilmente, zona plana función destornillador, abrebotellas, regla de hasta 5 cm en un lateral, parte central para tuercas de 11 medidas diferentes (desde 1/4 hasta 11) y práctica cuchara con dientes para uso de tenedor. Presentada en una bolsa individual.', '20458', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7082, 'Complete multi-tool with 16 functions in stainless steel. Includes: carabiner for easy carrying, flat area for screwdriver function, bottle opener, ruler up to 5 cm on one side, central part for nuts of 11 different sizes (from 1/4 to 11) and practical spoon/fork. Presented in an individual bag.', '20458', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7083, 'Silla plegable con cuerpo de aluminio y nevera integrada en asiento fabricada en RPET 600D, con costuras reforzadas, cierre de cremallera y prácticas asas de hombros para transporte, pudiendo guardarlas en el bolsillo específico. Con tira de refuerzo de nylon en las patas e interior isotermo PEVA. Presentada en bolsa individual.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20459', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7084, 'Folding chair with Aluminium body and cooler bag integrated in the seat. Made in RPET 600D, with reinforced seams, zip closure and practical shoulder handles for transport, which can be kept in the dedicated pocket. With nylon reinforcement strip on the legs and PEVA isothermal interior. Presented in an individual bag.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20459', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7085, 'Manta de picnic premium fabricada en suave y cálido polar fleece de 140g/m2, con base en resistente poliéster de 190g/m2. En tamaño 120 x 150 cm y con cinta de transporte para hombro fabricada en resistente nylon, con cierre ajustable de velcro y a juego con el color de la manta. Además, se pliega fácilmente y está disponible en varios colores. Presentada en bolsa individual reciclada.', '20460', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7086, 'Premium picnic blanket made of soft and warm 140g/m2 polar fleece, with a durable 190g/m2 polyester backing. In size 120 x 150 cm,and with shoulder carrying strap made of durable nylon, with adjustable velcro fastening and in matching colour with the blanket. Folds up easily and is available in several colours. Presented in an individual recycled bag.', '20460', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7087, 'Manta de picnic premium fabricada en resistente poliéster 190T RPET y acabado en negro. Plegable, con funda ajustable de autocierre en poliéster 190T RPET. En tamaño 120x150 cm, de fácil plegado y tamaño compacto, con logotipo RPET.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20461', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7088, 'Premium picnic blanket made of durable 190T RPET polyester with black finish. Foldable, with adjustable self closing sleeve in 190T RPET polyester. In size 120x150 cm, easy to fold and compact in size, with RPET logo.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20461', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7089, 'Bolsa impermeable de 5L de capacidad, fabricada en resistente 300D RPET. Cierre de seguridad, asa con hebilla de ajuste y mosquetón incluido. Etiqueta distintiva de RPET en el cierre. Disponible en variada gama de colores bitono. Presentada en bolsa individual reciclada. El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20462', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7090, 'Waterproof bag with 5L capacity, made of durable 300D RPET. Security zip, handle with buckle adjustment and carabiner included. Distinctive RPET logo on the closure. Available in a variety of two-tone colours, and presented in individual recycled bag.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20462', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7091, 'Bolsa impermeable de 10L de capacidad, fabricada en resistente poliéster 300D RPET. Cierre de seguridad, asa con hebilla de ajuste y mosquetón incluido. Etiqueta distintiva de RPET en el cierre. Disponible en variada gama de colores bitono. Presentada en bolsa individual reciclada. El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20463', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7092, 'Waterproof bag with 10L capacity, made of durable 300D RPET. Security zip, handle with buckle adjustment and carabiner included. Distinctive RPET logo on the closure. Available in a variety of two-tone colours, and presented in individual recycled bag.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20463', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7093, 'Mochila impermeable de gran capacidad (25L), fabricada en resistente RPET 300D. Con cierre de seguridad, asa con hebilla de ajuste, mosquetón, y cintas ajustables. Además, incluye ventana táctil frontal en transparente PVC. Con etiqueta distintiva de RPET en el cierre. Disponible en variada gama de colores y presentada en bolsa individual reciclada. El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20464', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7094, 'Large capacity waterproof backpack (25L), made of durable 300D RPET. With security lock, handle with buckle adjustment, carabiner, and adjustable straps. Additionally, it includes a front touch window in transparent PVC. With distinctive RPET label, available in a wide range of colours, and presented in an individual recycled bag. RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20464', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7095, 'Bolso impermeable de 25L de capacidad, confeccionado en resistente PVC. Compartimento principal con cierre de hebilla y asas ajustables para un cómodo transporte. Incluye bolsillo frontal abierto de fácil acceso. En elegante color gris con detalles en negro y presentado en bolsa individual reciclada.', '20465', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7096, 'Waterproof bag with 25L capacity, made of durable PVC. Main compartment with buckle closure and adjustable handles for comfortable carrying. Includes open front pocket for easy access. In stylish grey color with black accents, and presented in an individual recycled bag.', '20465', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7097, 'Bolso impermeable de 25L de capacidad, confeccionado en resistente PVC. Compartimento principal con triple cierre de hebilla, con asas para un cómodo transporte. En elegante color gris con detalles en negro y presentado en bolsa individual reciclada.', '20466', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7098, 'Waterproof bag with 25L capacity, made of durable PVC. Main compartment with triple buckle closure, with handles for comfortable carrying. In stylish grey color with black accents, and presented in an individual recycled bag.', '20466', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7099, 'Mochila nevera para picnic confeccionada en resistente RPET 600D de alta calidad. Diseñada para 2 personas, cuenta con un bolsillo independiente para un almacenaje seguro. Incluye 2 vasos, 2 cuchillos, 2 cucharas, 2 tenedores y 2 platos. Además, presenta un bolsillo frontal de rápido acceso y bolsillos laterales de rejilla. El compartimento nevera tiene un interior en PEVA, y la mochila cuenta con una cómoda asa de transporte, cintas y parte trasera acolchadas. Logotipo RPET en etiqueta.El material RPET se elabora a partir de plástico reciclado, para así fomentar la reutilización de residuos plásticos y ayudar a la sostenibilidad del planeta.', '20467', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7100, 'Picnic cooler backpack made in durable, high quality 600D RPET. Designed for 2 people, with an independent pocket for safe storage. Including 2 glasses, 2 knives, 2 spoons, 2 forks and 2 dishes. Also features a quick access front pocket and side mesh pockets. The cooler compartment has a PEVA interior, and the backpack has a comfortable carry handle, padded straps and padded back. RPET label.RPET material is made from recycled plastic, thus promoting the reuse of plastic waste and helping the sustainability of the planet.', '20467', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7101, 'Eleva el estilo de tus picnics con esta cesta elegante y retro. Elaborada en mimbre y algodón de acabado natural, cuenta con una resistente asa de transporte en polipiel. Diseñada para 2 personas, incluye 2 copas, 2 cuchillos, 2 cucharas, 2 tenedores, 2 platos y dos servilletas de tela. Se presenta de manera individual en una bolsa reciclada.', '20468', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7102, 'Elevate the style of your picnics with this chic, retro basket. Crafted from wicker and cotton in a natural finish, featuring a strong PU leather carry handle. Designed for 2 people, including 2 glasses, 2 knives, 2 spoons, 2 forks, 2 dishes and 2 cloth napkins. Presented in an individual recycled bag.', '20468', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7103, 'Set de barbacoa elaborado en bambú y acero inox. Este conjunto incluye 7 accesorios de metal con mangos de bambú: 1 cuchillo, trinchador, pinzas, espátula, salero y pimentero, junto con una tabla de bambú para corte y presentación. El set se presenta de manera elegante en un estuche individual fabricado en resistente poliéster, con sujeciones elásticas interiores para los distintos utensilios.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20469', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7104, 'Barbecue set made of bamboo and stainless steel. This set includes 7 metal accessories with bamboo handles: knife, carving knife, tongs, spatula, salt and pepper containers, together with a bamboo cutting and presentation board. The set is elegantly presented in an individual case made of durable polyester, with elastic fastenings on the inside for the different utensils.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20469', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7105, 'Set de barbacoa fabricado en acero inox. Consta de 11 accesorios, entre ellos: 1 espátula lisa, 2 espátulas con agujeros, 1 raspadora, 1 pinza, 1 espátula, 1 salero, 1 pimentero, 1 bidón con dosificador y 2 moldes redondos. Todo se presenta de manera organizada en un práctico macuto con cierre de cremallera y asas para facilitar su transporte. Presentado individualmente en bolsa reciclada.', '20470', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7106, 'Barbecue set made of stainless steel. Including 11 accessories: 1 smooth spatula, 2 spatulas with holes, 1 scraper, 1 set of tongs, 1 spatula, 1 salt shaker, 1 pepper shaker, 1 measuring can with dispenser and 2 round molds. Everything is presented in an organised way, inside a practical bag with zip closure and handles for easy carrying. Presented in an individual recycled bag.', '20470', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7107, 'Práctico flexómetro fabricado en resistente combinación de bambú y ABS, con multiherramienta integrada en cuerpo y cierre mediante tapa. 4 funciones: linterna LED (pilas de botón incluidas), una cinta métrica de 1 metro en centí metros y pulgadas; y un cabezal destornillador magnético con puntas plana y phillips. Además, cuenta con anilla llavero y viene presentado individualmente en caja de diseño kraft. Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20471', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7108, 'Handy flexometer made of resistant combination of bamboo and ABS, with integrated multi-tool in body and lid closure. 4 functions: LED torch (button batteries included), 1-metre tape measure -in centimetres and inches-, and a magnetic screwdriver head with flat and phillips bits. Also comes with a key ring and is presented individually in a kraft box.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20471', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7109, 'Práctica multiherramienta fabricada en resistente acero al carbono de acabado en color negro, con carcasa de bambú y 7 funciones: llaves allen, destornillador plano y philips. Presentada en caja de diseño kraft. Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20472', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7110, 'Practical multi-tool made of resistant carbon steel with black finish, with bamboo casing and 7 functions: allen keys, flat and philips screwdriver. Presented in a kraft design box.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20472', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7111, 'Multiherramienta con 7 funciones, fabricada en acero inox y bambú. Incluye un metro con medidas en centí metros y pulgadas (longitud de 1 metro), una llave para tuercas hexagonales en varios tamaños y un abridor. Además, cuenta con un mosquetón para facilitar el transporte. Presentada en caja de diseño kraft.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20474', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7112, 'Multi-tool with 7 functions, made of stainless steel and bamboo. Includes a tape measure in centimetres and inches ( 1 metre in length), a spanner for hexagonal nuts in various sizes and an opener. It also has a carabiner for easy transport. Presented in a kraft design box.Materials such as bamboo encourage the use of natural raw materials, reducing pollutant emissions. It comes from a highly resistant and versatile plant with rapid growth and regeneration, making of it an excellent alternative to traditional wood.', '20474', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7113, 'Completo set de jardinería con 7 accesorios y delantal ajustable con bolsillos. Incluye 2 palas de diferentes tamaños, dos rastrillos, un punzón, tijeras de podar y guantes para proteger las manos. Presentado en caja de diseño kraft.', '20475', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7114, 'Complete gardening set with 7 accessories and adjustable apron with pockets. Including 2 shovels in different sizes, two rakes, an awl, pruning shears and gloves. Presented in a kraft design box.', '20475', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7115, 'Práctica multiherramienta de 5 funciones, fabricada en resistente acero al carbono y acabado en color negro. Con funciones destornillador (punta plana y philips) y bolígrafo con tinta azul (cartucho jumbo). Presentada en resistente estuche de bambú, con nivel integrado y regla. Presentada individualmente en caja de diseño kraft.', '20477', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7116, 'Practical multi-tool with 5 functions, made of resistant carbon steel and finished in black. With screwdriver functions (flat and philips tip) and ball pen with blue ink (jumbo cartridge). Presented in a durable bamboo case, with integrated level and ruler. Individually packed in a kraft design box.', '20477', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7117, 'Práctico bolígrafo multifunción con 7 usos. Incluye: soporte para móvil, puntero, bolígrafo de tinta azul, linterna alimentada por pila de botón (incluida), brújula, destornillador plano y philips. Presentado individualmente en una caja de diseño kraft.', '20478', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7118, 'Handy multifunctional pen with 7 uses. Includes: mobile phone holder, stylus, blue ink pen, torch powered by button cell battery (included), compass, flat and philips screwdriver. Presented individually in a kraft design box.', '20478', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7119, 'Práctico flexómetro multiherramienta, fabricado en caña de trigo. Esta herramienta versátil ofrece 6 funciones en las que incluye: metro en pulgadas y centí metros (longitud de 1 metro) y función destornillador con 3 puntas planas y 3 philips, todas guardadas en la empuñadura con ventana transparente. Con logotipo caña de trigo incluido. Presentado en caja individual.La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20479', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7120, 'Handy multi-tool flexometer, made of wheat straw. This versatile tool offers 6 functions including: inch and centimetre measuring tape ( 1 metre in length) and screwdriver function with 3 flat and 3 philips bits, all stored inside the transparent windowed handle. With wheat straw logo included. Presented in an individual box.Wheat straw promotes the use of natural raw materials, thus reducing pollutant emissions.', '20479', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7121, 'Práctica multiherramienta fabricada en caña de trigo con forma de destornillador. Incluye un compartimento en la empuñadura para guardar las múltiples puntas magenéticas (planas y philips). Con logotipo caña de trigo incluido. Presentada en caja de diseño kraft. La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20480', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7122, 'Practical multi-tool made of wheat straw in the shape of a screwdriver. Including a compartment inside the handle for storing multiple magnetic bits (flat and philips). With a wheat straw logo included and presented in a kraft design box.Wheat straw promotes the use of natural raw materials, thus reducing pollutant emissions.', '20480', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7123, 'Excelente multiherramienta fabricada en aluminio y acero inox con acabado en color negro. Incluye 13 funciones diferentes: llave para tuercas hexagonales en varios tamaños, mosquetón, abrelatas, regla, rasqueta, cuchillo, destornilladores planos y philips. Presentada en caja de diseño kraft.', '20481', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7124, 'Superb multi-tool made of Aluminium and stainless steel with black finish. With 13 different functions: spanner for hex nuts in various sizes, carabiner, can opener, ruler, scraper, knife, flat and phillips screwdrivers. Presented in a kraft design box.', '20481', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7125, 'Eficaz multiherramienta con cabezal giratorio fabricada en acero inox y acabado en negro. Con 10 funciones: llaves allen, llave para tuercas hexagonales en varios tamaños, regla y un práctico accesorio de transporte con cierre de hebilla. Presentado en bolsa de papel reciclada de diseño kraft.', '20482', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7126, 'Efficient multi-tool with swivel head made of stainless steel with black finish. With 10 functions: allen spanners, hexagon spanner in various sizes, ruler and a practical carrying accessory with buckle fastener. Presented in a recycled kraft paper bag.', '20482', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7127, 'Set de herramientas presentado en resistente caja de bambú con cierre metálico. Con interior troquelado en bambú para un perfecto ajuste de las herramientas. Incluye 15 accesorios: llaves de tubo, alicates con sección cortante, destornilladores de precisión (plano y philips) y destornillador con multitud de puntas planas, philips, hexagonales y torx. Presentado en caja de diseño kraft. Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20483', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7128, 'Tool set presented in a resistant bamboo box with metal closure. With bamboo die-cut interior for a perfect fit of the tools. Including 15 accessories: socket spanners, pliers with cutting function, precision screwdrivers (flat and phillips) and screwdriver with a multitude of flat, phillips, hex and torx bits. Presented in a kraft design box.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20483', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7129, 'Navaja multiusos fabricada en acero inox y suave bambú. Con 9 funciones que incluyen cuchillo, tijeras, abrelatas, sacacorchos, destornillador philips, destornillador plano, lima y abrebotellas. Además, cuenta con un práctico enganche en forma de mosquetón. Presentada en caja de diseño kraft. Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20484', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7130, 'Multi-purpose knife made of stainless steel and soft bamboo. With 9 functions including knife, scissors, can opener, corkscrew, philips screwdriver, flat screwdriver, file and bottle opener. It also has a practical carabiner clip. Presented in a kraft design box.Materials such as bamboo encourage the use of natural raw materials, reducing pollutant emissions. It comes from a highly resistant and versatile plant with rapid growth and regeneration, making of it an excellent alternative to traditional wood.', '20484', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7131, 'Potente linterna abridor fabricada en resistente abs y 30 luces COB, con tres posiciones de luz (fija e intermitente de dos velocidades). Incluye mosquetón para fácil transporte, soporte trasero de apoyo, abrebotellas y potente imán. Recargable, con batería de 300 mAh. Cable incluido y manual en español e ingles. Presentado individualmente en caja de diseño.', '20485', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7132, 'Powerful opener torch made of resistant ABS. 30 COB lights, with three light positions ( steady and flashing with two modes). Includes carabiner for easy carrying, rear support bracket, bottle opener and powerful magnet. Rechargeable, with 300 mAh battery. Cable included and manual in English and Spanish. Presented individually in a design box.', '20485', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7133, 'Práctica multiherramienta fabricada en resistente aluminio de acabado en negro y 12 funciones. Con linterna de 20 luces COB y 3 posiciones de iluminación: luz fija con dos grados de intensidad y luz intermitente; y alimentada por una batería de 500 mAh. Incluye llaves para tuercas hexagonales en varios tamaños, abridor, y martillo rompe cristales. Con función destornillador (incluidas puntas planas y philips) y un potente imán. Práctico mosquetón de enganche y soporte en la parte trasera. Con cable de carga incluido y presentado en caja de diseño kraft.', '20486', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7134, 'Practical multi-tool made of robust aluminium with black finish and 12 functions. With 20 COB lights and 3 lighting positions: steady light with two levels of intensity and flashing light; powered by a 500 mAh battery. Includes spanners for hex nuts in various sizes, opener, and glass breaker hammer. With screwdriver function (including flat and philips bits) and powerful magnet. Practical carabiner hook and holder on the back. With charging cable included and presented in a kraft design box.', '20486', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7135, 'Práctica multiherramienta fabricada en acero inox y acabado en negro. Diseño plano en forma de tarjeta y con 16 funciones: destornilladores planos, abridor de cartas, regla en cm y pulgadas, abridor de botellas, abrelatas, pelador, llave para tuercas hexagonales en varios tamaños y linterna (pila de botón incluida). Presentada en bolsa individual diseño en papel reciclado.', '20487', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7136, 'Handy multi-tool made of stainless steel and finished in black. Flat card-shaped design with 16 functions: flat screwdrivers, letter opener, ruler in cm and inches, bottle opener, can opener, peeler, spanner for hex nuts in various sizes and torch (button battery included). Presented in an individual bag made of recycled paper.', '20487', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7137, 'Linterna potente fabricada en aluminio con un diseño elegante en acabado gris metalizado con detalles en negro. Cuenta con 1 LED frontal para una iluminación focalizada y 4 luces COB en el mango para una dispersión más amplia de la luz. Ofrece 3 modos de iluminación: fija, intermitente lenta y rápida, adaptándose a diversas necesidades. Esta linterna es recargable mediante su batería de 1200 mAh, con el cable de carga incluido. Presentada individualmente en caja de diseño kraft.', '20488', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7138, 'Powerful torch made of Aluminium with a classy design in a metallic grey finish with black accents. It has 1 front LED for spot illumination and 4 COB lights in the handle for flood light. It features 3 lighting modes: steady, slow flashing and fast flashing, adapting to different needs. This torch is rechargeable via 1200 mAh battery, with charging cable included. Presented individually in a kraft design box.', '20488', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7139, 'Linterna de aluminio práctica y versátil, con cabezal extensible (triplicando su longitud) y flexible para llegar a todos los lugares, además está equipado con imanes adaptándose a diversas necesidades. Cuenta con 3 potentes LEDs para una iluminación eficiente. Funciona con pilas de botón incluidas y presenta un clip metálico para facilitar su sujeción. Se presenta de manera individual en una caja de diseño kraft.', '20489', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7140, 'Practical and versatile Aluminium torch, with extendable head (tripling its length) and flexible to reach everywhere. It is equipped with magnets to adapt to different needs and has 3 powerful LEDs for efficient illumination. Powered by button batteries -included- as well as metal clip. Individually packaged in a kraft design box.', '20489', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7141, 'Linterna solar de mano, fabricada en resistente ABS. Su versatilidad con su doble función de carga: mediante la placa solar incorporada y de forma manual gracias a la dinamo integrada, ofreciendo una fuente de energía limpia y renovable reduciendo el consumo de pilas. Equipada con 3 potentes luces LEDs y un cordón que asegura un agarre firme para mayor comodidad. Presentada en caja de diseño kraft.', '20490', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7142, 'Handheld solar torch, made of resistant ABS. Versatile and with dual charging function: via the built-in solar panel and manually using the integrated dynamo, providing a clean, renewable source of energy and reducing battery consumption. Equipped with 3 powerful LED lights and a lanyard that ensures a firm grip and comfort. Presented in a kraft design box.', '20490', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7143, 'Linterna eco-amigable con 2 LEDs de carga manual mediante la dinamo incorporada; simplemente apretando el gatillo con una mano se genera energía, ofreciendo una fuente de luz sin generar residuos. En elegante color negro y gris, incluyeendo una cinta de transporte para mayor comodidad. Presentada en caja de diseño kraft.', '20491', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7144, 'Eco-friendly torch with 2 LEDs, manually charged via the built-in dynamo. By just pulling the trigger with one hand, power is generated, providing a zero-waste source of light. In stylish black and grey colors, and including a convenient carrying strap. Presented in a kraft design box.', '20491', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7145, 'Potente linterna con batería recargable, fabricada en aluminio de acabado en color negro. Luz frontal con 1 LED y cabezal regulable, para tener un enfoque más focalizado o abierto. Además, cuenta con 10 luces COB en el mango y tres modos de iluminación (fija e intermitente de dos velocidades), incluye cinta de trasporte. Se alimenta mediante una batería recargable de 400 mAh, incluido cable micro USB. Presentada individualmente en caja de diseño.', '20492', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7146, 'Powerful torch with rechargeable battery, made of Aluminium with black finish. Front light with 1 LED and adjustable head, for spot or flood light. Including 10 COB lights in the handle and three lighting modes ( steady and two-speed flashing). Powered by a 400 mAh rechargeable battery, including micro USB cable. Presented individually in a design box.', '20492', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7147, 'Set de herramientas de 24 accesorios en estuche de corcho con interior ordenado por elásticos. Incluye llaves de tubo, destornilladores de precisión (plano y philips), mango multifunción con diversas puntas intercambiables (extensión, cabezales philips, planos, de estrella y adaptador), cúter y un metro. Presentado en caja de diseño kraft.Elaborado con elementos vegetales para así fomentar la utilización de materias primas naturales y reducir las emisiones contaminantes.', '20493', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7148, '24-accessory tool set in a cork case with inside elastic bands. Includes socket spanners, precision screwdrivers (flat and philips), multifunction handle with various interchangeable bits (extension, philips and flat; and adapters); and a tape measure. Presented in a kraft design box. Made with plant-based elements to promote the use of natural raw materials and reduce polluting emissions.', '20493', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7149, 'Flexómetro 1 m fabricado en bambú, con cuchilla metálica en centí metros y pulgadas. Con cinta para transporte y presentado en caja de diseño kraft. Materiales como el bambú fomentan la utilización de materias primas naturales reduciendo las emisiones contaminantes. Además, el bambú proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápida, siendo una excelente alternativa al uso de madera tradicional.', '20494', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7150, '1 m flexometer made of bamboo, with metal blade in centimetres and inches. With carrying strap and presented in a kraft design box.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20494', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7151, 'Práctico llavero multifunción fabricado en bambú, con abridor y útiles niveles integrados. Presentado individualmente en caja de diseño kraft. Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20495', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7152, 'Practical multifunctional key ring made of bamboo, with integrated opener and useful level meters. Individually presented in a kraft design box.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20495', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7153, 'Práctica multiherramienta fabricada en resistente ABS y acero inox, con linterna frontal integrada de 1 LED. Incluye linterna lateral de 19 luces COB con dos posiciones de ilumincación (fija e intermitente). Dispone de 13 funciones, entre ellas cuchillo liso, de sierra, destornillador philips, tijeras, destornilladores planos, llave para tuercas hexagonales en varios tamaños, abrelatas, abrebotellas y una lima. Con imán de fijacion y asa. Alimentada con 3 pilas AAA, no incluidas. Presentada en caja de diseño kraft.', '20496', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7154, 'Practical multi-tool made of durable ABS and stainless steel, with integrated 1 LED headlamp. Includes 19 COB side torch with two lighting positions ( steady and flashing ). It has 13 functions, including flat knife, serrated knife, philips screwdriver, scissors, flat screwdrivers, hexagon spanner in various sizes, can opener, bottle opener and a file. With magnet and handle. Powered by 3 AAA batteries, not included. Presented in a kraft design box.', '20496', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7155, 'Set de herramientas en estuche de ABS con linterna incorporada y mango ergonómico, provisto con 4 Leds alimentados por 3 pilas AAA (no incluidas). Dispone de 21 accesorios en su interior: destornilladores de precisión (planos y philips), mango multifunción para cabezales compatibles (philips, planos, estrella, adaptador), llaves de tubo y flexómetro. Presentado en caja de diseño kraft.', '20497', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7156, 'Tool set in ABS case with built-in torch and ergonomic handle, supplied with 4 LEDs powered by 3 AAA batteries (not included). It has 21 accessories inside: precision screwdrivers (flat and philips), multifunction tool handle for compatible bits (philips, flat, adapters), socket spanners and flexometer. Presented in a kraft design box.', '20497', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7157, 'Práctico set de herramientas fabricado en caña de trigo y troquelado interior para un perfecto ajuste. Incluye un destornillador de precisión con 24 accesorios, compuesto por diversas puntas: philips, allen y planas. Con logotipo caña de trigo incluido. Presentado individualmente en caja de diseño kraft. La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20498', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7158, 'Practical tool set made of wheat straw, with die-cut inside for a perfect fit. Includes a precision screwdriver with 24 accessories, consisting of various bits: philips, allen and flat bits. With a wheat straw logo included and presented in a kraft design box.Wheat straw promotes the use of natural raw materials, thus reducing pollutant emissions.', '20498', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7159, 'Cutter con cuchilla ancha fabricado en caña de trigo. Mango ergonómico troquelado con capucha extraíble para ajuste de corte de cuchilla, con mecanismo de bloqueo para mayor seguridad. Con logotipo caña de trigo incluido. Presentado individualmente en bolsa de papel reciclado. La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20499', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7160, 'Cutter with wide blade made of wheat straw. Ergonomic die-cut handle with removable hood for blade cutting and safety locking mechanism. With a wheat straw logo included and presented in a kraft design box.Wheat straw promotes the use of natural raw materials, thus reducing pollutant emissions.', '20499', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7161, 'Flexómetro de 5 metros con cuchilla metálica y mecanismo de bloqueo. Con cuerpo en original acabado transparente, clip metálico para cinturón y cinta de transporte, con zona especial de marcaje. Presentado individualmente en caja de diseño kraft.', '20500', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7162, '5 metre flexometer with metal blade and locking mechanism. With original transparent finish body, metal belt clip and carrying strap, with special branding area. Individually packed in a kraft design box.', '20500', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7163, 'Cutter metálico con carcasa fabricada en bambú. Con mango ergonómico troquelado y clip extraíble con ranura para corte de cuchillas. Presentado individualmente en bolsa de papel reciclado. Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20501', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7164, 'Metal cutter with bamboo housing. With ergonomic die-cut handle and removable clip with slot for cutting blades. Individually packed in a recycled paper bag.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20501', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7165, 'Práctica multiherramienta fabricada en aluminio, con nivel integrado en el cuerpo y dos cabezales para destornillador (plano y philips) en el interior. Con clip metálico de acabado cromado y presentada en caja de diseño kraft.', '20503', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7166, 'Practical multitool made of Aluminium, with integrated level meter in the housing and two screwdriver heads (flat and philips) inside. With chrome-plated metal clip and presented in a kraft design box.', '20503', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7167, 'Bolígrafo multifunción 8 en 1 de mecanismo giratorio, fabricado en acero inox. Incluye regla en cm y pulgadas, nivel, abridor, 1 LED alimentado por pila de botón (incluida), puntero, punta plana y philips. Con clip metálico y tinta azul. Presentado individualmente en caja de diseño kraft.', '20504', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7168, 'Multifunctional 8-in-1 ball pen with twist mechanism, made in stainless steel. Including ruler in cm and inches, level, opener, 1 LED powered by button cell battery (included), pointer, and flat and philips bits. With metal clip and blue ink. Presented individually in a kraft design box.', '20504', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7169, 'Cinta para la cabeza fabricada en suave poliéster. Esta banda elástica de alta calidad destaca por sus vivos colores y su ancho de 8 cm. Presentación en bolsa individual.', '20505', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7170, 'High quality headband made of soft polyester. It stands out due to its bright colours and a width of 8 cm. Presented in an individual bag.', '20505', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7171, 'Gorra para niño de 5 paneles en 100% Algodón Orgánico. Cierre ajustable de velcro de color natural. Etiqueta en interior con logo Algodón Orgánico.', '20506', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7172, 'Kid cap with 5 panels in 100% organic cotton. Adjustable velcro fastening in natural colour. Inside label with organic cotton logo.', '20506', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7173, 'Gorra para niño de 6 paneles en 100% Algodón Orgánico. Cierre ajustable de hebilla metálica en color bronce. Etiqueta en interior con logo Algodón Orgánico.', '20507', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7174, 'Kid cap with 6 panels in 100% organic cotton. Adjustable closure with bronze coloured metal buckle. Inside label with organic cotton logo.', '20507', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7175, 'Flexómetro multiherramienta 3m, fabricado en resistente combinación de bambú y ABS. Con 7 funciones: cinta métrica de 3 metros, destornillador con diversas puntas (planas, philips y allen); y nivel integrado. De elegante diseño en negro, con detalles en bambú y viene presentado individualmente en caja de diseño kraft.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20508', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7176, '3m multi-tool flexometer, made of durable combination of bamboo and ABS. With 7 functions: 3 metre tape measure, screwdriver with a variety of bits (flat, philips and allen) and integrated level meter. Elegantly designed in black with bamboo details, and presented individually in a kraft design box.Materials such as bamboo promote the use of natural raw materials, thus reducing pollutant emissions. It comes from a highly resistant and versatile plant, with rapid growth and regeneration, making it an excellent alternative to the use of traditional wood.', '20508', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7177, 'Taza de cerámica de 350 ml. Disponible en diversos colores, con zona especial de marcaje para sublimación alrededor de la taza. Presentada individualmente en caja kraft.', '20509', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7178, '350 ml ceramic mug. Available in different colours, with special sublimation printing area all around the mug. Presented individually in a kraft box.', '20509', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7179, 'Jarra de cristal de acabado mate. De 500 ml de capacidad y con cómoda asa. Especialmente diseñada para la sublimación. Apta para lavavajillas, microondas y presentada en caja individual kraft.', '20510', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7180, 'Glass jar with matt finish. With a capacity of 500 ml and a convenient handle. Specially designed for sublimation. Dishwasher and microwave safe and presented in an individual kraft box.', '20510', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7181, 'Vaso de cristal de acabado mate de 330 ml de capacidad. Especialmente diseñado para la sublimación y presentado en caja individual kraft.', '20511', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7182, 'Glass tumbler with a matt finish and a capacity of 330 ml. Specially designed for sublimation. Presented in an individual kraft box.', '20511', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7183, 'Taza de cristal de borosilicato de acabado mate de 350 ml de capacidad. Especialmente diseñada para la sublimación. Disponible en diversos y llamativos colores translúcidos y presentada en caja individual kraft.Fabricado en cristal de borosilicato, que ofrece una resistencia y durabilidad excepcionales, alargando su vida útil y pudiendo ser reutilizado infinidad de veces.', '20512', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7184, 'Matt finish borosilicate glass mug with a capacity of 350 ml. Specially designed for sublimation. Available in a variety of striking and translucent colours. Presented in an individual kraft box.Made of borosilicate glass, which offers exceptional strength and durability, extending lifespan and able to be reused countless times.', '20512', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7185, 'Jarra de cerámica en elegante acabado blanco para los más cerveceros, de 700 ml de capacidad y con cómoda asa. Especialmente diseñada para el marcaje en sublimación y presentada en caja individual kraft.', '20513', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7186, 'Ceramic jar in elegant white finish for the beer lovers. 700 ml capacity and with comfortable handle. Specially designed for sublimation printing and presented in an individual kraft box.', '20513', null);
