INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7239, 'Completo set de baño con 4 accesorios. Practica bolsa de algodón de autocierre que incluye: cepillo para pelo fabricado en suave madera con cerdas a juego, suave esponja, esponja exfoliante adaptable a la palma de la mano y piedra pómez.', '20543', null);
