INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7241, 'Práctico cepillo exfoliante en madera natural. Cuatro funciones diferentes en cada extremo del cepillo: en un extremo con piedra pómez y lija metálica; y en el otro extremo con lija natural y suaves cerdas. Con práctico cordón para colgarlo después de su uso.', '20544', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7242, 'Useful exfoliating brush in natural wood. Four different functions at each end of the brush: at one end with pumice stone and metal file; at the other end with natural file and soft bristles. With practical string to hang it up after use.', '20544', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7243, 'Práctico cepillo de dientes portátil eléctrico. Eficiente limpieza gracias a su función de vibración y a sus agradables cerdas. Funciona con 1 pilas AA (no incluida) introducidas por la base. En color blanco y plateado, presentado en una caja de diseño kraft.', '20545', null);
