INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7245, 'Práctico cepillo de dientes plegable fabricado en resistente caña de trigo. Incluye compartimento para guardar un tubo de pasta de dientes de 6g (incluido).La caña de trigo fomenta la utilización de materias primas naturales, reduciendo así las emisiones contaminantes.', '20546', null);
