INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7250, 'Original pastillero mensual fabricado en PP resistente, con 31 casillas independientes y extraíbles para guardar todas las pastillas necesarias para el día. Con base blanca y cajas grises impresas con el día del mes. Presentado en bolsa individual.', '20548', null);
