INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7253, 'Parche térmico reutilizable con efecto frío o calor. El efecto frío se consigue introduciéndolo en el frigorífico entre 2-3 horas, mientras que el efecto calor en requiere del microondas. Reutilizable gracias a las perlas de gel ubicadas en su interior, y con cinta ajustable a la palma de la mano de forma rectangular. Presentación individual en una bolsa de papel reciclado.', '20552', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7254, 'Reusable thermal patch with cold or heat effect. The cold effect is achieved by placing it in the refrigerator for 2-3 hours, while the heat effect requires a microwave oven. Reusable thanks to the gel beads inside,and with an adjustable rectangular hand strap to fit in the palm of the hand. Presented individually in a recycled paper bag.', '20552', null);
