INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7334, 'Abridor de botellas fabricado en bambú, con imán para poder adherirlo a cualquier superficie metálica. Presentado individualmente caja de diseño kraft.Materials such as bamboo encourage the use of natural raw materials, reducing pollutant emissions. In addition, bamboo comes from a highly resistant and versatile plant, with rapid growth and regeneration, making of it an excellent alternative to traditional wood.', '20594', null);
