INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7348, 'Scented candle in metal jar and in elegant colours, with vanilla fragrance. Presented in an individual kraft design box.', '20601', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7349, 'Vela aromática con perfume vainilla. Presentada en tarro de metal con tapadera fabricada en bambú. Tarro disponible en variada gama de colores de acabado metalizado. Presentada individualmente en caja de diseño kraft.Materiales como el bambú fomentan la utilización de materias primas naturales, reduciendo las emisiones contaminantes. Proviene de una planta de gran resistencia y versatilidad, con crecimiento y regeneración rápidos, siendo una excelente alternativa al uso de madera tradicional.', '20602', null);
INSERT INTO grupoalan.descriptions (id, details, ref, product_id) VALUES (7350, 'Vanilla scented candle. Presented in a metal jar with a bamboo lid. Jar available in a wide range of colours, with metal finish. Presented individually in a kraft design box.Materials such as bamboo encourage the use of natural raw materials and reduce pollutant emissions. In addition, bamboo comes from a highly resistant and versatile plant, with rapid growth and regeneration, making of it an excellent alternative to the use of traditional wood.', '20602', null);
